@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
  }

  /* Enhanced typography classes with new fonts */
  .story-text {
    @apply font-serif text-lg leading-relaxed text-pretty tracking-wide;
  }

  .story-title {
    @apply font-serif text-4xl font-semibold text-balance tracking-tight;
  }

  .heading-display {
    @apply font-sans text-5xl font-bold text-balance tracking-tight;
  }

  .heading-large {
    @apply font-sans text-3xl font-semibold text-balance tracking-tight;
  }

  .body-large {
    @apply font-sans text-lg leading-relaxed text-pretty;
  }

  .body-medium {
    @apply font-sans text-base leading-relaxed text-pretty;
  }

  /* Enhanced gradient with better color harmony */
  .magical-gradient {
    background: linear-gradient(135deg, oklch(0.65 0.25 45) 0%, oklch(0.55 0.3 280) 50%, oklch(0.45 0.25 200) 100%);
  }

  .competition-gradient {
    background: linear-gradient(135deg, oklch(0.7 0.3 320) 0%, oklch(0.6 0.35 340) 50%, oklch(0.65 0.25 45) 100%);
  }

  .card-shadow {
    box-shadow: 0 8px 32px -4px oklch(0.65 0.25 45 / 0.15), 0 4px 16px -4px oklch(0.55 0.3 280 / 0.1);
  }

  .competition-shadow {
    box-shadow: 0 12px 40px -8px oklch(0.7 0.3 320 / 0.25), 0 6px 20px -6px oklch(0.6 0.35 340 / 0.15);
  }
}


