const specialElementOptions = [
  {
    id: "dialogue-heavy",
    label: "Rich Dialogue",
    description: "Character-driven conversations",
    icon: "💬",
  },
  {
    id: "twist-ending",
    label: "Surprise Ending",
    description: "Unexpected plot twist",
    icon: "🔄",
  },
  {
    id: "moral-lesson",
    label: "Life Lesson",
