"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ThemeSwitcher } from "@/components/theme-switcher";
import {
  BookOpen,
  Plus,
  TrendingUp,
  Eye,
  Heart,
  Award,
  Star,
  Sparkles,
  Calendar,
  Edit3,
  Loader2,
} from "lucide-react";
import { createClientComponentClient } from "@supabase/auth-helpers-nextjs";
import { useRouter } from "next/navigation";

interface DashboardStats {
  totalStories: number;
  totalViews: number;
  totalLikes: number;
  weeklyCreated: number;
}

interface StoryMetric {
  id: string;
  title: string;
  views: number;
  likes: number;
  createdAt: string;
  template: string;
  readTime: number;
}

export default function Dashboard() {
  const [stats, setStats] = useState<DashboardStats>({
    totalStories: 0,
    totalViews: 0,
    totalLikes: 0,
    weeklyCreated: 0,
  });
  const [loading, setLoading] = useState(true);
  const [recentStories, setRecentStories] = useState<StoryMetric[]>([]);
  const [topStories, setTopStories] = useState<StoryMetric[]>([]);
  const [editedStories, setEditedStories] = useState<StoryMetric[]>([]);
  const supabase = createClientComponentClient();
  const router = useRouter();

  const logout = async () => {
    await supabase.auth.signOut();
    router.push("/login");
  };

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const { data: { user } } = await supabase.auth.getUser();

        if (!user) {
          router.push('/login');
          return;
        }

        // Fetch user's stories with better error handling
        const { data: stories, error } = await supabase
          .from('stories')
          .select('*')
          .eq('user_id', user.id)
          .order('created_at', { ascending: false });

        if (error) {
          console.error('Error fetching stories:', error);
          // Use fallback data if database query fails
          setStats({ totalStories: 0, totalViews: 0, totalLikes: 0, weeklyCreated: 0 });
          setRecentStories([]);
          setTopStories([]);
          setEditedStories([]);
        } else {
          const storyData = stories || [];
          const oneWeekAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);

          // Calculate stats from real data
          const totalViews = storyData.reduce((sum: number, story: any) => sum + (story.views || 0), 0);
          const totalLikes = storyData.reduce((sum: number, story: any) => sum + (story.likes || 0), 0);
          const weeklyCreated = storyData.filter((story: any) => 
            new Date(story.created_at) > oneWeekAgo
          ).length;

          setStats({
            totalStories: storyData.length,
            totalViews,
            totalLikes,
            weeklyCreated,
          });

          // Process stories for different sections
          const processedStories = storyData.map((story: any) => ({
            id: story.id,
            title: story.title || 'Untitled Story',
            views: story.views || 0,
            likes: story.likes || 0,
            createdAt: story.created_at,
            template: story.template || 'story',
            readTime: Math.ceil((story.content?.split(' ').length || 600) / 200),
          }));

          setRecentStories(processedStories.slice(0, 3));
          setTopStories([...processedStories].sort((a, b) => b.views - a.views).slice(0, 6));
          setEditedStories([...processedStories].filter((story: any) => 
            story.updated_at && story.createdAt !== story.updated_at
          ).slice(0, 2));
        }
      } catch (error) {
        console.error('Dashboard fetch error:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [supabase, router]);

  const getTemplateColor = (template: string) => {
    switch (template) {
      case "kids":
        return "bg-amber-100 text-amber-800 dark:bg-amber-900 dark:text-amber-200";
      case "adventure":
        return "bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-200";
      case "inspirational":
        return "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200";
      default:
        return "bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-200";
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      month: "short",
      day: "numeric",
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-background">
        <header className="border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
          <div className="container mx-auto px-4 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <BookOpen className="h-6 w-6 text-primary" />
                <span className="font-bold text-xl bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent">StoryBuds</span>
              </div>
              <ThemeSwitcher />
            </div>
          </div>
        </header>
        <main className="container mx-auto px-4 py-8">
          <div className="space-y-8">
            <div className="animate-pulse">
              <div className="h-8 bg-muted rounded w-1/3 mb-2"></div>
              <div className="h-4 bg-muted rounded w-1/2"></div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {[...Array(4)].map((_, i) => (
                <div key={i} className="animate-pulse">
                  <div className="h-32 bg-muted rounded"></div>
                </div>
              ))}
            </div>

            <div className="animate-pulse">
              <div className="h-48 bg-muted rounded"></div>
            </div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Link href="/" className="flex items-center space-x-3">
                <img
                  src="/StoryBuds/StoryBuds-logo-transparent.png"
                  alt="StoryBuds"
                  className="h-8 w-auto object-contain"
                />
                <span className="font-bold text-xl bg-gradient-to-r from-primary to-secondary bg-clip-text text-transparent">
                  StoryBuds
                </span>
              </Link>
              <nav className="hidden md:flex items-center space-x-6">
                <Link href="/dashboard" className="text-primary font-medium">
                  Dashboard
                </Link>
                <Link
                  href="/generate"
                  className="text-muted-foreground hover:text-foreground transition-colors"
                >
                  Create
                </Link>
                <Link
                  href="/bucket"
                  className="text-muted-foreground hover:text-foreground transition-colors"
                >
                  My Shelf
                </Link>
                <Link
                  href="/community"
                  className="text-muted-foreground hover:text-foreground transition-colors"
                >
                  Community
                </Link>
              </nav>
            </div>
            <div className="flex items-center gap-4">
              <ThemeSwitcher />
              <button 
                className="text-neutral-700 hover:text-foreground transition-colors" 
                onClick={logout}
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        {/* Welcome Section */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-foreground mb-2">
            Welcome back, Storyteller!
          </h1>
          <p className="text-muted-foreground">
            Ready to create your next magical story?
          </p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">
                Total Stories
              </CardTitle>
              <BookOpen className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.totalStories}</div>
              <p className="text-xs text-muted-foreground">
                +{stats.weeklyCreated} this week
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Views</CardTitle>
              <Eye className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {stats.totalViews.toLocaleString()}
              </div>
              <p className="text-xs text-muted-foreground">
                Across all stories
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Likes</CardTitle>
              <Heart className="h-4 w-4 text-muted-foreground" />
