import { createRouteHandlerClient } from "@supabase/auth-helpers-nextjs";
import { cookies } from "next/headers";
import { NextResponse } from "next/server";

export async function GET(
  req: Request,
  { params }: { params: { id: string } }
) {
  const supabase = createRouteHandlerClient({ cookies });

  try {
    const { id } = params;

    if (!id) {
      return NextResponse.json({ error: "Story ID is required" }, { status: 400 });
    }

    // Check if user is authenticated
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser();

    if (userError || !user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    // Fetch the story
    const { data: story, error } = await supabase
      .from("stories")
      .select("*")
      .eq("id", id)
      .eq("user_id", user.id)
      .single();

    if (error) {
      if (error.code === "PGRST116") {
        return NextResponse.json({ error: "Story not found" }, { status: 404 });
      }
      return NextResponse.json({ error: error.message }, { status: 400 });
    }

    // Optionally increment view count (if we want to track views)
    await supabase
      .from("stories")
      .update({ views: (story.views || 0) + 1 })
      .eq("id", id);

    return NextResponse.json({ story });
  } catch (err: any) {
    return NextResponse.json(
      { error: err.message || "Internal Server Error" },
      { status: 500 }
    );
  }
}

export async function PUT(
  req: Request,
  { params }: { params: { id: string } }
) {
  const supabase = createRouteHandlerClient({ cookies });

  try {
    const { id } = params;
    const updates = await req.json();

    if (!id) {
      return NextResponse.json({ error: "Story ID is required" }, { status: 400 });
    }

    // Check if user is authenticated
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser();

    if (userError || !user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    // Validate allowed fields for updates
    const allowedFields = ["story_title", "content", "type", "cover_image"];
    const filteredUpdates = Object.keys(updates)
      .filter(key => allowedFields.includes(key))
      .reduce((obj, key) => {
        obj[key] = updates[key];
        return obj;
      }, {} as any);

    if (Object.keys(filteredUpdates).length === 0) {
      return NextResponse.json({ error: "No valid fields to update" }, { status: 400 });
    }

    // Add updated_at timestamp
    filteredUpdates.updated_at = new Date().toISOString();

    // Update the story
    const { data: story, error } = await supabase
      .from("stories")
      .update(filteredUpdates)
      .eq("id", id)
      .eq("user_id", user.id)
      .select()
      .single();

    if (error) {
      if (error.code === "PGRST116") {
        return NextResponse.json({ error: "Story not found" }, { status: 404 });
      }
      return NextResponse.json({ error: error.message }, { status: 400 });
    }

    return NextResponse.json({ story });
  } catch (err: any) {
    return NextResponse.json(
      { error: err.message || "Internal Server Error" },
      { status: 500 }
    );
  }
}

export async function DELETE(
  req: Request,
  { params }: { params: { id: string } }
) {
  const supabase = createRouteHandlerClient({ cookies });

  try {
    const { id } = params;

    if (!id) {
      return NextResponse.json({ error: "Story ID is required" }, { status: 400 });
    }

    // Check if user is authenticated
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser();

    if (userError || !user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    // Delete the story
    const { error } = await supabase
      .from("stories")
      .delete()
      .eq("id", id)
      .eq("user_id", user.id);

    if (error) {
      return NextResponse.json({ error: error.message }, { status: 400 });
    }

    return NextResponse.json({ message: "Story deleted successfully" });
  } catch (err: any) {
    return NextResponse.json(
      { error: err.message || "Internal Server Error" },
      { status: 500 }
    );
  }
}

app/api/v1/story/generate/route.ts.content:
// /api/generate/route.ts
import { NextResponse } from "next/server";

export async function POST(req: Request) {
  const { fullPrompt, mode = "full" } = await req.json();

  const resp = await fetch("https://mlvoca.com/api/generate", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      model: "deepseek-r1:1.5b",
      prompt: fullPrompt,
    }),
  });

  if (!resp.ok || !resp.body) {
    return NextResponse.json(
      { error: "Failed to reach LLM API" },
      { status: 502 }
    );
  }

  const decoder = new TextDecoder();
  let fullStory = "";

  if (mode === "stream") {
    const encoder = new TextEncoder();
    const stream = new ReadableStream({
      async start(controller) {
        const reader = resp.body!.getReader();
        while (true) {
          const { done, value } = await reader.read();
          if (done) break;

          const chunk = decoder.decode(value, { stream: true });
          fullStory += chunk;
          controller.enqueue(encoder.encode(chunk));
        }
        controller.close();
      },
    });

    return new Response(stream, {
      headers: { "Content-Type": "text/plain; charset=utf-8" },
    });
  }

  // Wait until LLM fully finishes
  const reader = resp.body!.getReader();
  while (true) {
    const { done, value } = await reader.read();
    if (done) break;
    fullStory += decoder.decode(value, { stream: true });
  }

  if (!fullStory.trim()) {
    return NextResponse.json(
      { error: "LLM returned empty story" },
      { status: 500 }
    );
  }

  return NextResponse.json({ story: fullStory });
}

app/api/v1/story/save/route.ts.content:
import { createRouteHandlerClient } from "@supabase/auth-helpers-nextjs";
import { cookies } from "next/headers";
import { NextResponse } from "next/server";

export async function POST(req: Request) {
  const supabase = createRouteHandlerClient({ cookies });

  try {
    const { story, prompt, storyLength, selectedTemplate } = await req.json();

    // ✅ Ensure user is logged in
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser();

    if (userError || !user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    // ✅ Insert into stories table
    const { data, error } = await supabase
      .from("stories")
      .insert([
        {
          story_title: prompt || "Untitled Story",
          content: story,
          type: selectedTemplate?.type || "custom",
          prompt,
          length: storyLength,
          cover_image: null, // placeholder for now
          user_id: user.id,
        },
      ])
      .select()
      .single();

    if (error) {
      return NextResponse.json({ error: error.message }, { status: 400 });
    }

    return NextResponse.json({ story: data }, { status: 201 });
  } catch (err: any) {
    return NextResponse.json(
      { error: err.message || "Internal Server Error" },
      { status: 500 }
    );
  }
}

components/ui/pdf-builder.tsx.content:
"use client";

import { useState } from "react";
import { jsPDF } from "jspdf";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd"; // modern fork of react-beautiful-dnd
import { Button } from "@/components/ui/button";

// Templates
const templates = [
  { id: "classic", name: "Classic", font: "Times", style: "Normal" },
  { id: "modern", name: "Modern", font: "Helvetica", style: "Bold" },
  { id: "vintage", name: "Vintage", font: "Courier", style: "Italic" },
  { id: "colorful", name: "Colorful", font: "Helvetica", style: "Bold" },
];

interface StorySection {
  id: string;
  content: string;
}

interface PDFBuilderProps {
  story: string;
  title: string;
}

export function PDFBuilder({ story }: PDFBuilderProps) {
  // Split story into sections by paragraph
  const initialSections: StorySection[] = story
    .split("\n\n")
    .map((p, index) => ({ id: `section-${index}`, content: p }));

  const [storySections, setStorySections] = useState(initialSections);
  const [selectedTemplate, setSelectedTemplate] = useState(templates[0].id);

  // Update paragraph content
  const handleUpdateSection = (index: number, value: string) => {
    const newSections = [...storySections];
    newSections[index].content = value;
    setStorySections(newSections);
  };

  // Handle drag & drop
  const handleDragEnd = (result: any) => {
    if (!result.destination) return;
    const items = Array.from(storySections);
    const [reordered] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reordered);
    setStorySections(items);
  };

  // Generate PDF
  const handleGeneratePDF = () => {
    const doc = new jsPDF({ unit: "pt", format: "a4" });
    const margin = 40;
    const maxWidth = doc.internal.pageSize.getWidth() - margin * 2;
    let y = 60;

    // Apply template styles
    const template = templates.find((t) => t.id === selectedTemplate);
    if (!template) return;

    // Title
    doc.setFont(template.font as any, template.style as any);
    doc.setFontSize(24);
    doc.text("Your Story", doc.internal.pageSize.getWidth() / 2, y, { align: "center" });
    y += 40;

    // Paragraphs
    doc.setFontSize(12);
    storySections.forEach((section) => {
      const lines = doc.splitTextToSize(section.content, maxWidth);
      lines.forEach((line) => {
        if (y > doc.internal.pageSize.getHeight() - 60) {
          doc.addPage();
          y = 60;
        }
        doc.text(line, margin, y);
        y += 14;
      });
      y += 10; // spacing between paragraphs
    });

    doc.save(`story-${selectedTemplate}.pdf`);
  };

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      {/* Template Selector */}
      <div className="flex gap-4 mb-6">
        {templates.map((t) => (
          <button
            key={t.id}
            className={`px-4 py-2 rounded ${
              selectedTemplate === t.id
                ? "bg-primary text-white shadow-lg"
                : "bg-card border"
            } transition-all hover:scale-105`}
            onClick={() => setSelectedTemplate(t.id)}
          >
            {t.name}
          </button>
        ))}
      </div>

      {/* Drag & Drop Sections */}
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="story">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef} className="space-y-3">
              {storySections.map((section, index) => (
                <Draggable key={section.id} draggableId={section.id} index={index}>
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className={`p-4 bg-card rounded shadow transition-all ${
                        snapshot.isDragging ? "scale-105 shadow-xl" : ""
                      }`}
                    >
                      <textarea
                        className="w-full border-none focus:ring-0 bg-transparent resize-none text-sm"
                        value={section.content}
                        onChange={(e) => handleUpdateSection(index, e.target.value)}
                        rows={Math.max(2, section.content.split("\n").length)}
                      />
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>

      {/* Generate PDF */}
      <Button
        className="px-6 py-3 bg-gradient-to-r from-primary to-secondary text-white rounded shadow-lg hover:scale-105 transition-transform"
        onClick={handleGeneratePDF}
      >
        Generate PDF
      </Button>
    </div>
  );
}

