      attribution: 'William Shakespeare',
      alignment: 'center',
      style: 'simple',
      showQuotes: true,
      quoteStyle: 'curly',
      textStyle: 'italic',
      attributionStyle: 'normal'
    },
    icon: 'quote',
    required: false,
    maxPerBook: 100
  },
];

// Helper function to get a template by ID
export function getTemplateById(templateId: string): PageTemplate | null {
  return PAGE_TEMPLATES.find(template => template.id === templateId) || null;
}

// Helper function to get templates by category
export function getTemplatesByCategory(categoryId: string): PageTemplate[] {
  return PAGE_TEMPLATES.filter(template => template.category === categoryId);
}

// Re-export the createPageFromTemplate from book-utils to maintain backward compatibility
export { createPageFromTemplate } from './book-utils';

// Helper function to get the default pages for a new book
export function getDefaultPages(userId?: string): BookPage[] {
  const now = new Date().toISOString();

  const coverPage = createPageFromTemplate('cover', 1, {
    createdBy: userId || 'system',
    customMetadata: {
      status: 'draft',
      created: now,
      updated: now,
      tags: ['front-matter', 'cover']
    },
    customContent: {
      title: 'Book Title',
      subtitle: 'A Subtitle',
      author: 'Author Name'
    }
  });

  const titlePage = createPageFromTemplate('title', 2, {
    createdBy: userId || 'system',
    customMetadata: {
      status: 'draft',
      created: now,
      updated: now,
      tags: ['front-matter', 'title']
    }
  });

  const copyrightPage = createPageFromTemplate('copyright', 3, {
    createdBy: userId || 'system',
    customMetadata: {
      status: 'draft',
      created: now,
      updated: now,
      tags: ['front-matter', 'legal']
    },
    customContent: {
      copyrightText: ` ${new Date().getFullYear()} Author Name. All rights reserved.`,
      isbn: '000-0-0000-0000-0',
      publisher: 'Publisher Name'
    }
  });

  const tocPage = createPageFromTemplate('toc', 4, {
    createdBy: userId || 'system',
    customMetadata: {
      status: 'draft',
      created: now,
      updated: now,
      tags: ['front-matter', 'toc']
    },
    customContent: {
      title: 'Table of Contents',
      includePageNumbers: true,
      includeChapterTitles: true
    }
  });

  const chapter1Page = createPageFromTemplate('chapter', 5, {
    createdBy: userId || 'system',
    customMetadata: {
      status: 'draft',
      created: now,
      updated: now,
      tags: ['chapter', 'content']
    },
    customContent: {
      title: 'Chapter 1',
      content: 'Begin your story here...'
    }
  });

  // Set up navigation between pages
  coverPage.navigation.nextPageId = titlePage.id;

  titlePage.navigation.previousPageId = coverPage.id;
  titlePage.navigation.nextPageId = copyrightPage.id;

  copyrightPage.navigation.previousPageId = titlePage.id;
  copyrightPage.navigation.nextPageId = tocPage.id;

  tocPage.navigation.previousPageId = copyrightPage.id;
  tocPage.navigation.nextPageId = chapter1Page.id;

  chapter1Page.navigation.previousPageId = tocPage.id;

  return [coverPage, titlePage, copyrightPage, tocPage, chapter1Page];
};

lib/book-utils.ts.content:
import { v4 as uuidv4 } from "uuid";

/**
 * Types
 */
export interface PageContent {
  type: string;
  text?: string;
  level?: number;
  children?: PageContent[];
}

export interface PageMetadata {
  title: string;
  description?: string;
  wordCount: number;
  pageNumber: number;
  createdAt: string;
  updatedAt: string;
  createdBy: string;
  updatedBy: string;
}

export interface PageStyle {
  fontSize: number;
  fontFamily: string;
  lineHeight: number;
  alignment: "left" | "center" | "right" | "justify";
  margins: {
    top: number;
    right: number;
    bottom: number;
    left: number;
  };
  backgroundColor: string;
  textColor: string;
}

export interface BookPage {
  id: string;
  uuid: string;
  templateId: string;
  type: "content" | "toc" | "cover";
  title: string;
  content: PageContent[];
  metadata: PageMetadata;
  style: PageStyle;
  status: "draft" | "published" | "archived";
  createdAt: string;
  updatedAt: string;
  createdBy: string;
  updatedBy: string;
}

export interface BookData {
  id: string;
  uuid: string;
  title: string;
  author_name: string;
  pages: BookPage[];
  status: "draft" | "published" | "archived";
  created_at: string;
  updated_at: string;
  metadata: {
    title: string;
    author: string;
    description: string;
    language: string;
    status: "draft" | "published" | "archived";
    tags: string[];
    wordCount: number;
    pageCount: number;
    categories: string[];
    customFields: Record<string, string>;
  };
  settings: {
    fontFamily: string;
    fontSize: number;
    lineHeight: number;
    textColor: string;
    backgroundColor: string;
    margins: {
      top: number;
      right: number;
      bottom: number;
      left: number;
    };
    pageNumberStyle: {
      position: "header" | "footer";
      alignment: "left" | "center" | "right";
      fontSize: number;
      showOnFirstPage: boolean;
    };
  };
  collaborators: {
    id: string;
    role: "owner" | "editor" | "viewer";
    permissions: string[];
  }[];
  permissions: {
    canEdit: string[];
    canView: string[];
    canShare: string[];
  };
  export_settings: {
    pdf: {
      pageSize: "A4" | "Letter";
      margins: { top: number; right: number; bottom: number; left: number };
      includeToc: boolean;
      includePageNumbers: boolean;
    };
    epub: { includeMetadata: boolean; compressImages: boolean };
    html: { includeCss: boolean; inlineStyles: boolean };
  };
  custom_fields: Record<string, string>;
}

/**
 * Utils
 */
export const updatePageNumbers = (pages: BookPage[]): BookPage[] =>
  pages.map((page, index) => ({
    ...page,
    metadata: {
      ...page.metadata,
      pageNumber: index + 1,
    },
  }));

export const createPageFromTemplate = (
    templateId: string,
    type: BookPage["type"],
    title: string,
    content: PageContent[],
    userId = "system"
): any => {
  const now = new Date().toISOString();
  return {
    id: `page-${Date.now()}-${Math.random()}`,
    uuid: uuidv4(),
    templateId,
    type,
    title,
    content,
    metadata: {
      title,
      description: "",
      wordCount: content.reduce(
        (acc, c) => acc + (c.text ? c.text.split(/\s+/).length : 0),
        0
      ),
      pageNumber: 0, // assigned later
      createdAt: now,
      updatedAt: now,
      createdBy: userId,
      updatedBy: userId,
    },
    style: {
      fontSize: 12,
      fontFamily: "Times New Roman",
      lineHeight: 1.5,
      alignment: "left",
      margins: { top: 72, right: 72, bottom: 72, left: 72 },
      backgroundColor: "#ffffff",
      textColor: "#000000",
    },
    status: "draft",
    createdAt: now,
    updatedAt: now,
    createdBy: userId,
    updatedBy: userId,
  };
};

export function createDefaultBook(): BookData {
  const now = new Date().toISOString();
  const userId = "system";

  const defaultBook: BookData = {
    id: `book-${Date.now()}`,
    uuid: uuidv4(),
    title: "Untitled Book",
    author_name: "Unknown Author",
    pages: [],
    status: "draft",
    created_at: now,
    updated_at: now,
    metadata: {
      title: "Untitled Book",
      author: "Unknown Author",
      description: "A new book created with the book builder",
      language: "en-US",
      status: "draft",
      tags: ["draft"],
      wordCount: 0,
      pageCount: 0,
      categories: ["Uncategorized"],
      customFields: {},
    },
    settings: {
      fontFamily: "Times New Roman",
      fontSize: 12,
      lineHeight: 1.5,
      textColor: "#000000",
      backgroundColor: "#ffffff",
      margins: { top: 72, right: 72, bottom: 72, left: 72 },
      pageNumberStyle: {
        position: "footer",
        alignment: "center",
        fontSize: 10,
        showOnFirstPage: false,
      },
    },
    collaborators: [
      {
        id: userId,
        role: "owner",
        permissions: ["edit", "view", "share"],
      },
    ],
    permissions: {
      canEdit: [userId],
      canView: [userId],
      canShare: [userId],
    },
    export_settings: {
      pdf: {
        pageSize: "A4",
        margins: { top: 72, right: 72, bottom: 72, left: 72 },
        includeToc: true,
        includePageNumbers: true,
      },
      epub: { includeMetadata: true, compressImages: true },
      html: { includeCss: true, inlineStyles: true },
    },
    custom_fields: {},
  };

  // Add a sample page
  const samplePage = createPageFromTemplate(
    "default",
    "content",
    "Chapter 1",
    [
      {
        type: "paragraph",
        text: "This is your first page. Start writing your story here!",
      },
    ],
    userId
  );

  defaultBook.pages = updatePageNumbers([samplePage]);

  return defaultBook;
}

/**
 * Table of Contents
 */
export interface TocEntry {
  title: string;
  pageNumber: number;
  level: number;
}

export const generateTableOfContents = (pages: BookPage[]): TocEntry[] =>
  pages
    .filter((p) => p.type === "content" && p.title)
    .map((p) => ({
      title: p.title,
      pageNumber: p.metadata.pageNumber,
      level: 1,
    }));

export const createTocPage = (entries: TocEntry[]): BookPage => {
  const now = new Date().toISOString();
  return {
    id: `toc-${Date.now()}`,
    uuid: uuidv4(),
    templateId: "table-of-contents",
    type: "toc",
    title: "Table of Contents",
    content: entries.map((entry) => ({
      type: "toc-entry",
      text: entry.title,
      children: [
        { type: "page-number", text: entry.pageNumber.toString() },
      ],
    })),
    metadata: {
      title: "Table of Contents",
      description: "Generated automatically",
      wordCount: entries.length,
      pageNumber: 0,
      createdAt: now,
      updatedAt: now,
      createdBy: "system",
      updatedBy: "system",
    },
    style: {
      fontSize: 12,
      fontFamily: "Times New Roman",
      lineHeight: 1.5,
      alignment: "left",
      margins: { top: 72, right: 72, bottom: 72, left: 72 },
      backgroundColor: "#ffffff",
      textColor: "#000000",
    },
    status: "draft",
    createdAt: now,
    updatedAt: now,
    createdBy: "system",
    updatedBy: "system",
  };
};

/**
 * Book manipulation
 */
export const addPage = (
  book: BookData,
  page: BookPage,
  position?: number
): BookData => {
  const pages = [...book.pages];
  if (position === undefined || position < 0 || position > pages.length) {
    pages.push(page);
  } else {
    pages.splice(position, 0, page);
  }
  return { ...book, pages: updatePageNumbers(pages) };
};

export const removePage = (book: BookData, pageId: string): BookData => {
  const pages = book.pages.filter((p) => p.id !== pageId);
  return { ...book, pages: updatePageNumbers(pages) };
};

export const movePage = (
  book: BookData,
  fromIndex: number,
  toIndex: number
): BookData => {
  const pages = [...book.pages];
  const [moved] = pages.splice(fromIndex, 1);
  pages.splice(toIndex, 0, moved);
  return { ...book, pages: updatePageNumbers(pages) };
};

/**
 * Metadata Export
 */
export const generateBookMetadata = (book: BookData): string => {
  const escapeXml = (str: string) =>
    str
      .replace(/&/g, "&amp;")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;")
      .replace(/"/g, "&quot;")
      .replace(/'/g, "&apos;");

  return `<?xml version="1.0" encoding="UTF-8"?>
<metadata>
  <title>${escapeXml(book.metadata.title)}</title>
  <author>${escapeXml(book.metadata.author)}</author>
  <language>${book.metadata.language}</language>
  <description>${escapeXml(book.metadata.description)}</description>
  <status>${book.metadata.status}</status>
  <wordCount>${book.metadata.wordCount}</wordCount>
  <pageCount>${book.metadata.pageCount}</pageCount>
  <tags>${book.metadata.tags
    .map((tag) => `<tag>${escapeXml(tag)}</tag>`)
    .join("")}</tags>
  <categories>${book.metadata.categories
    .map((cat) => `<category>${escapeXml(cat)}</category>`)
    .join("")}</categories>
</metadata>`;
};

types/book.ts.content:
import { PageTemplate } from "@/lib/book-templates";

export interface BookMetadata {
  title?: string;
  subtitle?: string;
  author?: string;
  description?: string;
  language?: string;
  publisher?: string;
  publishedDate?: string;
  isbn?: string;
  version?: string;
  updated?: string;
  status?: 'draft' | 'published' | 'archived';
  tags?: string[];
  categories?: string[];
  coverImage?: string;
  wordCount?: number;
  pageCount?: number;
  customFields?: Record<string, any>;
}

// Define possible page types
export type PageType = 
  // Front Matter
  'cover' | 'title' | 'copyright' | 'dedication' | 'toc' | 'foreword' | 'preface' | 'acknowledgments' | 'introduction' | 'prologue' |
  // Main Content
  'chapter' | 'section' | 'subchapter' | 'part' | 'act' | 'scene' |
  // Back Matter
  'epilogue' | 'afterword' | 'appendix' | 'glossary' | 'bibliography' | 'index' | 'colophon' | 'back-cover' |
  // Special Pages
  'blank' | 'divider' | 'quote' | 'about-author' | 'testimonials' |
  // Custom
  'custom';

// Define page statuses
export type PageStatus = 'draft' | 'in-review' | 'approved' | 'published' | 'archived';

// Define page content types
export interface PageContent {
  // Core content
  blocks?: any[]; // Rich text editor blocks
  rawHtml?: string; // Raw HTML content
  markdown?: string; // Markdown content

  // Versioning
  version: number;
  lastModified: string;
  lastModifiedBy: string;

  // Page-specific content
  title?: string;
  subtitle?: string;
  author?: string;
  chapterNumber?: number | string;
  chapterTitle?: string;

  // Special content types
  copyrightText?: string;
  isbn?: string;
  publisher?: string;

  // TOC specific
  includePageNumbers?: boolean;
  includeChapterTitles?: boolean;

  // Custom fields
  customFields?: Record<string, any>;
}

export interface PageMetadata {
  created: string;
  updated: string;
  createdBy?: string;
  updatedBy?: string;
  status: PageStatus;
  tags: string[];
  wordCount?: number;
  characterCount?: number;
  readingTime?: number; // in minutes
  seo?: {
    description?: string;
    keywords?: string[];
    slug?: string;
  };
  customFields?: Record<string, any>;
  customMetadata: {
    status: 'draft',
    tags: ['cover']
  }
}

export interface PageStyles {
  // Layout
  width?: string;
  height?: string;
  margin?: string | {
    top: string;
    right: string;
    bottom: string;
    left: string;
  };
  padding?: string;

  // Typography
  fontFamily?: string;
  fontSize?: string;
  lineHeight?: number | string;
  textAlign?: 'left' | 'center' | 'right' | 'justify';
  textColor?: string;
  backgroundColor?: string;

  // Advanced
  columns?: number;
  columnGap?: string;
  breakBefore?: 'auto' | 'always' | 'avoid' | 'left' | 'right';
  breakAfter?: 'auto' | 'always' | 'avoid' | 'left' | 'right';
  breakInside?: 'auto' | 'avoid';

  // Print-specific
  printBackground?: boolean;
  printMargins?: {
    top: string;
    right: string;
    bottom: string;
    left: string;
  };
}

export interface PageNavigation {
  // Hierarchical navigation
  parentId: string | null;
  firstChildId: string | null;
  lastChildId: string | null;
  previousSiblingId: string | null;
  nextSiblingId: string | null;

  // Flat navigation (for sequential reading)
  previousPageId: string | null;
  nextPageId: string | null;

  // Structural information
  depth: number;
  path: string[]; // Array of ancestor IDs

  // Position in the book
  position: {
    absolute: number; // Absolute position in the book
    chapter: number; // Position within the current chapter
    section: number; // Position within the current section
  };

  // Navigation state
  isExpanded?: boolean;
  isVisibleInToc?: boolean;

  // Custom navigation properties
  customNavProperties?: Record<string, any>;
}

export interface BookPage {
  // Core properties
  id: string;
  uuid: string;
  templateId: string;
  type: PageType;
  title: string;
  subtitle?: string;

  // Content
  content: PageContent;

  // Layout & Styling
  layout: string;
  styles: PageStyles;

  // Metadata
  metadata: PageMetadata;

  // Navigation & Structure
  navigation: PageNavigation;

  // Visibility & State
  isVisible: boolean;
  isLocked?: boolean;
  lockedBy?: string;
  lockedAt?: string;

  // Versioning
  version: number;
  history?: Array<{
    version: number;
    timestamp: string;
    author: string;
    changes: string[];
  }>;

  // Page numbering
  pageNumber?: number | string; // Can be a number or Roman numerals for front matter
  pageNumberStyle?: {
    position?: 'header' | 'footer' | 'none';
    alignment?: 'left' | 'center' | 'right';
    format?: '1' | 'i' | 'I' | 'a' | 'A';
    startAt?: number;
    showInToc?: boolean;
  };

  // Advanced
  customFields?: Record<string, any>;
  permissions?: {
    view?: string[];
    edit?: string[];
    delete?: string[];
  };
}

export interface BookTheme {
  id: string;
  name: string;
  styles: {
    fontFamily: string;
    fontSize: string;
    lineHeight: number;
    textColor: string;
    backgroundColor: string;
    linkColor: string;
    headingFont: string;
    headingColor: string;
    paragraphSpacing: number;
    // Add more theme properties as needed
  };
  isDefault?: boolean;
  createdAt?: string;
  updatedAt?: string;
}

export interface BookData {
  id?: string;
  uuid?: string;
  title: string;
  author_name: string;
  description?: string;
  pages: BookPage[];
  theme_id?: string;
  status?: 'draft' | 'published' | 'archived';
  created_at?: string;
  updated_at?: string;
  published_at?: string;
  version?: string;
  metadata?: BookMetadata;
  settings?: {
    page_size?: 'A4' | 'Letter' | 'A5' | 'B5' | 'Executive' | 'Legal' | 'Tabloid';
    orientation?: 'portrait' | 'landscape';
    show_page_numbers?: boolean;
    margin?: {
      top: string;
      right: string;
      bottom: string;
      left: string;
    };
    header?: {
      enabled: boolean;
      content?: string;
      height?: string;
    };
    footer?: {
      enabled: boolean;
      content?: string;
      height?: string;
    };
  };
  collaborators?: Array<{
    id: string;
    role: 'owner' | 'editor' | 'viewer';
    name: string;
    email: string;
    avatar?: string;
    joined_at: string;
    last_active?: string;
  }>;
  permissions?: {
    can_edit?: boolean;
    can_export?: boolean;
    can_share?: boolean;
    can_delete?: boolean;
  };
  export_settings?: BookExportOptions;
  custom_fields?: Record<string, any>;
}

export interface BookExportOptions {
  format: 'pdf' | 'epub' | 'mobi' | 'docx' | 'html' | 'print';
  includeCover: boolean;
  includeToc: boolean;
  pageSize: 'A4' | 'Letter' | 'A5' | 'B5' | 'Executive' | 'Legal' | 'Tabloid';
  margin: {
    top: string;
    right: string;
    bottom: string;
    left: string;
  };
  printOptions?: {
    bleed?: string;
    cropMarks?: boolean;
    colorProfile?: string;
  };
  customCss?: string;
  customJs?: string;
}

app/book-builder/page.tsx.content:
"use client";

import { useState, useEffect, useCallback, useRef } from "react";
import { useRouter } from "next/navigation";
import { createClientComponentClient } from "@supabase/auth-helpers-nextjs";
import { v4 as uuidv4 } from 'uuid';
import { 
  Download, 
  Save, 
  Plus, 
  Trash2, 
  Copy, 
  ArrowUp, 
  ArrowDown, 
  GripVertical,
  Settings,
  BookOpen,
  FileText,
  Image as ImageIcon,
  Type,
  LayoutGrid,
  Bookmark,
  List,
  FileArchive,
  FileCheck,
  FileCode,
  FileX,
  FileSearch,
  FileSpreadsheet,
  FileType2,
  FileWarning,
  FileQuestion,
  FileJson,
  FileOutput,
  FileInput as FileInputIcon,
  FileVideo,
  FileImage,
  FileUp,
  FileDown,
  FileTerminal,
  FileType,
  ChevronLeft,
  ChevronRight,
  Maximize2,
  Minimize2,
  ZoomIn,
  ZoomOut,
  LayoutDashboard,
  Type as TypeIcon,
  Palette,
  BookOpenCheck,
  BookText
} from "lucide-react";
import { toast } from "sonner";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

// Import our enhanced book components
import { EnhancedSidebar } from "@/components/book-builder/EnhancedSidebar";
import { EnhancedPageEditor } from "@/components/book-builder/EnhancedPageEditor";
import { EnhancedBookPreview } from "@/components/book-builder/EnhancedBookPreview";

// Import our types and utilities
import { BookData, BookPage, BookTheme, PageType } from "@/types/book";
import { PAGE_TEMPLATES, getTemplateById } from "@/lib/book-templates";
import { createDefaultBook, createPageFromTemplate } from "@/lib/book-utils";

// Define the type for our active section
type ActiveSection = 'pages' | 'settings' | 'preview';

export default function BookBuilderPage() {
  const router = useRouter();
  const supabase = createClientComponentClient();
  const previewRef = useRef<HTMLDivElement>(null);

  // State for the book builder
  const [user, setUser] = useState<any>(null);
  const [selectedStoryId, setSelectedStoryId] = useState<string>("");
  const [bookData, setBookData] = useState<BookData>(() => {
    const defaultBook = createDefaultBook();
    return {
      ...defaultBook,
      pages: defaultBook.pages.map((page, index) => ({
        ...page,
        id: `page-${Date.now()}-${index}`,
        pageNumber: index + 1,
      })),
    };
  });

  // UI State
  const [activeSection, setActiveSection] = useState<ActiveSection>('pages');
  const [selectedPageIndex, setSelectedPageIndex] = useState<number>(0);
  const [themes, setThemes] = useState<BookTheme[]>([]);
  const [stories, setStories] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [validationErrors, setValidationErrors] = useState<string[]>([]);

  // Preview settings
  const [previewScale, setPreviewScale] = useState(0.8);
  const [showGuides, setShowGuides] = useState(true);
  const [showPageNumbers, setShowPageNumbers] = useState(true);
  const [isFullscreen, setIsFullscreen] = useState(false);

  // Get the current page
  const currentPage = bookData.pages[selectedPageIndex] || bookData.pages[0];

  // Create a stable reference to the current page
  const currentPageRef = useRef(currentPage);
  useEffect(() => {
    currentPageRef.current = currentPage;
  }, [currentPage]);

  // Load user and initial data
  useEffect(() => {
    loadInitialData();
  }, []);

  const loadInitialData = async () => {
    try {
      setIsLoading(true);

      // Get user
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        router.push('/login');
        return;
      }
      setUser(user);

      // Load themes
      const { data: themesData } = await supabase
        .from('book_themes')
        .select('*')
        .order('sort_order');

      if (themesData) {
        setThemes(themesData);
        if (themesData.length > 0) {
          setBookData(prev => ({
            ...prev,
            theme_id: themesData[0].id,
            cover_theme_id: themesData[0].id
          }));
        }
      }

      // Load user stories
      const { data: storiesData } = await supabase
        .from('stories')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false });

      if (storiesData) {
        setStories(storiesData);
      }

    } catch (error) {
      console.error('Error loading data:', error);
      toast.error('Failed to load data');
    } finally {
      setIsLoading(false);
    }
  };

  const handleStorySelect = async (storyId: string) => {
    if (!storyId) return;

    try {
      setIsLoading(true);
      const { data: story } = await supabase
        .from('stories')
        .select('*')
        .eq('id', storyId)
        .single();

      if (story) {
        setSelectedStoryId(storyId);

        // Create a new book with the story content
        const newBook = createDefaultBook();
        const now = new Date().toISOString();

        // Create cover page
        const coverPage = createPageFromTemplate(
          'cover',
          'cover',
          story.story_title || 'Untitled Story',
          [
            {
              type: 'paragraph',
              text: story.subtitle || ''
            },
            {
              type: 'paragraph',
              text: `By ${user?.user_metadata?.name || 'Author'}`
            }
          ],
          user?.id
        );

        // Create content page (using 'content' type since it's one of the valid types)
        const contentPage = createPageFromTemplate(
          'content',
          'content',
          'Chapter 1',
          [
            {
              type: 'heading',
              text: 'The Beginning',
              level: 1
            },
            {
              type: 'paragraph',
              text: story.content || 'Start writing your story here...'
            }
          ],
          user?.id
        );

        // Create end page
        const endPage = createPageFromTemplate(
          'content',
          'content',
          'The End',
          [
            {
              type: 'paragraph',
              text: 'Thank you for reading!'
            }
          ],
          user?.id
        );

        // Update the book data
        setBookData({
          ...newBook,
          title: story.story_title,
          author_name: user?.user_metadata?.name || "Author",
          pages: [coverPage, contentPage, endPage],
          updated_at: now
        });

        toast.success('Story loaded successfully!');
      }
    } catch (error) {
      console.error('Error loading story:', error);
      toast.error('Failed to load story');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSaveBook = async () => {
    if (!user) {
      toast.error('You must be logged in to save a book');
      return;
    }

    try {
      setIsSaving(true);

      // Prepare book data
      const now = new Date().toISOString();
      const bookToSave = {
        ...bookData,
        user_id: user.id,
        status: 'draft',
        total_pages: bookData.pages.length,
        updated_at: now,
        created_at: bookData.created_at || now,
      };

      // Remove any client-side only properties before saving
      delete (bookToSave as any).id;

      // Save book
      const { data: savedBook, error: bookError } = await supabase
        .from('books')
        .upsert({
          ...bookToSave,
          ...(bookData.id && { id: bookData.id }), // Only include ID if it exists
          story_id: selectedStoryId || null,
        })
        .select()
        .single();

      if (bookError) throw bookError;

      // Save pages
      const pagePromises = bookData.pages.map(async (page, index) => {
        const pageToSave = {
          ...page,
          book_id: savedBook.id,
          page_number: index + 1,
          updated_at: now,
          created_at: page.created_at || now,
        };

        // Remove any client-side only properties
        delete (pageToSave as any).id;

        const { error: pageError } = await supabase
          .from('book_pages')
          .upsert({
            ...pageToSave,
            ...(page.id && { id: page.id }), // Only include ID if it exists
          });

        if (pageError) throw pageError;
      });

      await Promise.all(pagePromises);

      // Update local state with saved data
      setBookData(prev => ({
        ...prev,
        id: savedBook.id,
        updated_at: now,
        created_at: savedBook.created_at || now,
      }));

      toast.success('Book saved successfully!');

      // Refresh the stories list to show the updated book
      if (selectedStoryId) {
        const { data: updatedStories } = await supabase
          .from('stories')
          .select('*')
          .eq('user_id', user.id)
          .order('created_at', { ascending: false });

        if (updatedStories) {
          setStories(updatedStories);
        }
      }

      return savedBook;

    } catch (error) {
      console.error('Error saving book:', error);
      toast.error('Failed to save book');
      throw error;
    } finally {
      setIsSaving(false);
    }
  };

  const handleExportPDF = async () => {
    if (!bookData.title) {
      toast.error('Please add a book title first');
      return;
    }

    try {
      setIsExporting(true);

      // First, save the book to ensure we have the latest version
      const savedBook = await handleSaveBook();

      if (!savedBook) {
        throw new Error('Failed to save book before export');
      }

      // Show a loading toast with a progress indicator
      const toastId = toast.loading('Preparing your book for export...');

      // Call the export API
      const response = await fetch('/api/v1/books/export-pdf', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${(await supabase.auth.getSession()).data.session?.access_token}`
        },
        body: JSON.stringify({
          bookId: savedBook.id,
          format: 'pdf',
          includeCover: true,
          includeToc: true,
          pageSize: 'A4',
          margin: {
            top: '2cm',
            right: '2.5cm',
            bottom: '2cm',
            left: '2.5cm'
          },
          printOptions: {
            bleed: '3mm',
            cropMarks: true,
            colorProfile: 'CMYK'
          }
        })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || 'Export failed');
      }

      // Get the blob and create a download link
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${bookData.title.replace(/[^a-zA-Z0-9]/g, '_')}_${new Date().toISOString().split('T')[0]}.pdf`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      toast.success('Book exported successfully!', { id: toastId });

    } catch (error) {
      console.error('Error exporting PDF:', error);
      toast.error(error instanceof Error ? error.message : 'Failed to export PDF');
    } finally {
      setIsExporting(false);
    }
  };

  // Handler for adding a new page
  const handleAddPage = (type: PageType = 'content') => {
    const newPage = createPageFromTemplate(type, bookData.pages.length + 1, {
      customContent: {
        title: type === 'chapter' ? `Chapter ${bookData.pages.filter(p => p.type === 'chapter').length + 1}` : 
              type === 'toc' ? 'Table of Contents' :
              type === 'cover' ? bookData.title :
              `Page ${bookData.pages.length + 1}`,
        content: type === 'toc' ? '' : 
                type === 'cover' ? '' : 
                `This is a new ${type} page. Start writing here...`,
      },
      customMetadata: {
        created: new Date().toISOString(),
        updated: new Date().toISOString(),
        status: 'draft',
        tags: [type, 'new']
      }
    });

    setBookData(prev => ({
      ...prev,
      pages: [...prev.pages, newPage]
    }));

    // Select the new page
    setSelectedPageIndex(bookData.pages.length);

    // Scroll to the new page in the preview
    setTimeout(() => {
      previewRef.current?.scrollIntoView({ behavior: 'smooth', block: 'end' });
    }, 100);
  };

  // Handler for deleting a page
  const handleDeletePage = (index: number) => {
    if (bookData.pages.length <= 1) {
      toast.error('Cannot delete the last page');
      return;
    }

    const newPages = [...bookData.pages];
    newPages.splice(index, 1);

    // Update page numbers
    const updatedPages = newPages.map((page, i) => ({
      ...page,
      pageNumber: i + 1
    }));

    setBookData(prev => ({
      ...prev,
      pages: updatedPages
    }));

    // Adjust selected index if needed
    if (selectedPageIndex >= index) {
      setSelectedPageIndex(Math.max(0, selectedPageIndex - 1));
    }

    toast.success('Page deleted');
  };

  // Handler for reordering pages
  const handlePageReorder = (oldIndex: number, newIndex: number) => {
    const newPages = [...bookData.pages];
    const [movedPage] = newPages.splice(oldIndex, 1);
    newPages.splice(newIndex, 0, movedPage);

    // Update page numbers
    const updatedPages = newPages.map((page, index) => ({
      ...page,
      pageNumber: index + 1
    }));

    setBookData(prev => ({
      ...prev,
      pages: updatedPages
    }));

    // Update selected index
    setSelectedPageIndex(newIndex);
  };

  // Handler for updating a page
  const handlePageUpdate = (updatedPage: BookPage) => {
    const newPages = [...bookData.pages];
    newPages[selectedPageIndex] = {
      ...updatedPage,
      updated_at: new Date().toISOString()
    };

    setBookData(prev => ({
      ...prev,
      pages: newPages,
      updated_at: new Date().toISOString()
    }));
  };

  // Handler for updating the entire book data
  const handleBookDataUpdate = (updates: Partial<BookData>) => {
    setBookData(prev => ({
      ...prev,
      ...updates,
      updated_at: new Date().toISOString()
    }));
  };

  // Loading state
  if (isLoading && !user) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-background">
        <div className="text-center space-y-4">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary mx-auto"></div>
          <h2 className="text-2xl font-bold text-foreground">Loading Book Builder</h2>
          <p className="text-muted-foreground">Preparing your workspace...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background flex flex-col">
      {/* Header */}
      <header className="bg-background border-b border-border px-6 py-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <BookOpenCheck className="h-6 w-6 text-primary" />
              <h1 className="text-xl font-bold text-foreground">Book Builder</h1>
            </div>

            <div className="relative w-64">
              <Select
                value={selectedStoryId ||'null'}
                onValueChange={handleStorySelect}
                disabled={isLoading || isSaving}
              >
                <SelectTrigger className="w-full">
                  <SelectValue placeholder="Select a story..." />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="e">Create New Book</SelectItem>
                  <div className="px-2 py-1.5 text-xs font-medium text-muted-foreground">
                    Your Stories
                  </div>
                  {stories.length > 0 ? (
                    stories.map((story) => (
                      <SelectItem key={story.id} value={story.id}>
                        <div className="flex items-center space-x-2">
                          <BookText className="h-4 w-4 text-muted-foreground" />
                          <span className="truncate">{story.story_title}</span>
                        </div>
                      </SelectItem>
                    ))
                  ) : (
                    <div className="px-3 py-2 text-sm text-muted-foreground">
                      No stories found
                    </div>
                  )}
                </SelectContent>
              </Select>
            </div>

            {bookData.title && (
              <div className="hidden md:flex items-center space-x-2">
                <span className="text-sm text-muted-foreground">Editing:</span>
                <span className="text-sm font-medium text-foreground truncate max-w-xs">
                  {bookData.title}
                </span>
              </div>
            )}
          </div>

          <div className="flex items-center space-x-2">
            <Button
              variant="outline"
              size="sm"
              onClick={handleSaveBook}
              disabled={isSaving || !bookData.title}
              className="flex items-center space-x-2"
            >
              <Save className="h-4 w-4" />
              <span className="hidden sm:inline">
                {isSaving ? 'Saving...' : 'Save'}
              </span>
            </Button>

            <Button
              variant="default"
              size="sm"
              onClick={handleExportPDF}
              disabled={isExporting || !bookData.title || bookData.pages.length === 0}
              className="flex items-center space-x-2"
            >
              <Download className="h-4 w-4" />
              <span className="hidden sm:inline">
                {isExporting ? 'Exporting...' : 'Export'}
              </span>
            </Button>

            <Button
              variant="ghost"
              size="icon"
              onClick={() => setIsFullscreen(!isFullscreen)}
              className="hidden md:flex"
            >
              {isFullscreen ? (
                <Minimize2 className="h-4 w-4" />
              ) : (
                <Maximize2 className="h-4 w-4" />
              )}
            </Button>
          </div>
        </div>
      </header>

      <div className="flex-1 flex overflow-hidden">
        {/* Enhanced Sidebar */}
        <EnhancedSidebar
          activeSection={activeSection}
          onSectionChange={setActiveSection as any}
          pages={bookData.pages}
          selectedPageIndex={selectedPageIndex}
          onPageSelect={setSelectedPageIndex}
          onAddPage={handleAddPage}
          onDeletePage={handleDeletePage}
          onPageReorder={handlePageReorder}
        />

        {/* Main Content Area */}
        <div className="flex-1 flex flex-col md:flex-row overflow-hidden">
          {/* Editor Panel */}
          {activeSection === 'pages' && (
            <div className={cn(
              "w-full md:w-96 lg:w-[28rem] border-r border-border bg-background overflow-y-auto",
              isFullscreen && 'hidden md:block'
            )}>
              {currentPage && (
                <EnhancedPageEditor
                  page={currentPage}
                  onPageChange={handlePageUpdate}
                  themes={themes}
                  className="h-full"
                />
              )}
            </div>
          )}

          {/* Settings Panel */}
          {activeSection === 'settings' && (
            <div className="w-full md:w-96 lg:w-[28rem] border-r border-border bg-background overflow-y-auto p-6">
              <h2 className="text-xl font-bold mb-6 text-foreground">Book Settings</h2>

              <div className="space-y-6">
                <div>
                  <Label htmlFor="book-title">Book Title</Label>
                  <Input
                    id="book-title"
                    value={bookData.title}
                    onChange={(e) => handleBookDataUpdate({ title: e.target.value })}
                    placeholder="Enter book title"
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label htmlFor="book-author">Author Name</Label>
                  <Input
                    id="book-author"
                    value={bookData.author_name}
                    onChange={(e) => handleBookDataUpdate({ author_name: e.target.value })}
                    placeholder="Enter author name"
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label>Book Description</Label>
                  <Textarea
                    value={bookData.description || ''}
                    onChange={(e) => handleBookDataUpdate({ description: e.target.value })}
                    placeholder="Enter a short description of your book"
                    className="mt-1 min-h-[100px]"
                  />
                </div>

                <div>
                  <Label>Theme</Label>
                  <Select
                    value={bookData.theme_id || '24378'}
                    onValueChange={(value) => handleBookDataUpdate({ theme_id: value })}
                  >
                    <SelectTrigger className="w-full mt-1">
                      <SelectValue placeholder="Select a theme" />
                    </SelectTrigger>
                    <SelectContent>
                      {themes && themes.length > 0 ? (
                        themes.map((theme) => {
                          const themeId = String(theme?.id || '').trim();
                          const themeName = String(theme?.name || 'Untitled Theme').trim();
                          return (
                            <SelectItem 
                              key={themeId || `theme-${Math.random().toString(36).substr(2, 9)}`} 
                              value={themeId || 'default'}
                            >
                              {themeName}
                            </SelectItem>
                          );
                        })
                      ) : (
                        <SelectItem value="no-themes" disabled>
                          No themes available
                        </SelectItem>
                      )}
                    </SelectContent>
                  </Select>
                </div>

                <div className="pt-4 border-t border-border">
                  <h3 className="font-medium text-foreground mb-3">Export Settings</h3>

                  <div className="space-y-4">
                    <div>
                      <Label>Page Size</Label>
                      <Select
                        value={bookData.page_size || 'A4'}
                        onValueChange={(value) => handleBookDataUpdate({ page_size: value })}
                      >
                        <SelectTrigger className="w-full mt-1">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="A4">A4 (210 × 297 mm)</SelectItem>
                          <SelectItem value="Letter">Letter (8.5 × 11 in)</SelectItem>
                          <SelectItem value="A5">A5 (148 × 210 mm)</SelectItem>
                          <SelectItem value="B5">B5 (176 × 250 mm)</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <Label>Orientation</Label>
                        <Select
                          value={bookData.orientation || 'portrait'}
                          onValueChange={(value) => handleBookDataUpdate({ orientation: value })}
                        >
                          <SelectTrigger className="w-full mt-1">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="portrait">Portrait</SelectItem>
                            <SelectItem value="landscape">Landscape</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>

                      <div>
                        <Label>Page Numbers</Label>
                        <Select
                          value={bookData.show_page_numbers ? 'show' : 'hide'}
                          onValueChange={(value) => handleBookDataUpdate({ show_page_numbers: value === 'show' })}
                        >
                          <SelectTrigger className="w-full mt-1">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="show">Show</SelectItem>
                            <SelectItem value="hide">Hide</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                    </div>

                    <div className="pt-2">
                      <Button 
                        variant="outline" 
                        className="w-full"
                        onClick={handleExportPDF}
                        disabled={isExporting}
                      >
                        <Download className="mr-2 h-4 w-4" />
                        {isExporting ? 'Exporting...' : 'Export Book'}
                      </Button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Preview Panel */}
          <div className={cn(
            "flex-1 bg-muted/30 overflow-hidden",
            activeSection === 'preview' ? 'block' : 'hidden md:block'
          )}>
            <EnhancedBookPreview
              book={{
                title: bookData.title || 'Untitled Book',
                pages: bookData.pages,
                theme_id: bookData.theme_id,
                // Pass the full theme object if available
                theme: themes.find(t => t.id === bookData.theme_id) || null
              }}
              currentPageIndex={selectedPageIndex}
              onPageChange={setSelectedPageIndex}
              scale={previewScale}
              className="h-full"
            />
          </div>
        </div>
      </div>

      {/* Mobile bottom navigation */}
      <div className="md:hidden fixed bottom-0 left-0 right-0 bg-background border-t border-border flex justify-around items-center py-2 px-4 z-10">
        <button
          onClick={() => setActiveSection('pages')}
          className={`flex flex-col items-center justify-center p-2 rounded-lg ${activeSection === 'pages' ? 'text-primary' : 'text-muted-foreground'}`}
        >
          <TypeIcon className="h-5 w-5" />
          <span className="text-xs mt-1">Edit</span>
        </button>

        <button
          onClick={() => setActiveSection('preview')}
          className={`flex flex-col items-center justify-center p-2 rounded-lg ${activeSection === 'preview' ? 'text-primary' : 'text-muted-foreground'}`}
        >
          <BookOpen className="h-5 w-5" />
          <span className="text-xs mt-1">Preview</span>
        </button>

        <button
          onClick={() => setActiveSection('settings')}
          className={`flex flex-col items-center justify-center p-2 rounded-lg ${activeSection === 'settings' ? 'text-primary' : 'text-muted-foreground'}`}
        >
          <Settings className="h-5 w-5" />
          <span className="text-xs mt-1">Settings</span>
        </button>
      </div>

      {/* Add some padding to account for the mobile navigation */}
      <div className="h-16 md:hidden"></div>
    </div>
  );
}

components/book-builder/BookBuilderSideb.content:
"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { 
  Settings, 
  FileText, 
  Eye, 
  Plus, 
  Trash2,
  ChevronDown,
  ChevronRight,
  Book,
  Image,
  Type
} from "lucide-react";

interface BookBuilderSidebarProps {
  activeSection: 'settings' | 'pages' | 'preview';
  onSectionChange: (section: 'settings' | 'pages' | 'preview') => void;
  pages: any[];
  selectedPageIndex: number;
  onPageSelect: (index: number) => void;
}

export function BookBuilderSidebar({
  activeSection,
  onSectionChange,
  pages,
  selectedPageIndex,
  onPageSelect
}: BookBuilderSidebarProps) {
  const [isPagesExpanded, setIsPagesExpanded] = useState(true);

  const getPageIcon = (pageType: string) => {
    switch (pageType) {
      case 'cover':
        return <Book className="w-4 h-4" />;
      case 'content':
        return <FileText className="w-4 h-4" />;
      case 'end':
        return <Type className="w-4 h-4" />;
      default:
        return <FileText className="w-4 h-4" />;
    }
  };

  const getPageTypeLabel = (pageType: string) => {
    switch (pageType) {
      case 'cover':
        return 'Cover';
      case 'content':
        return 'Content';
      case 'end':
        return 'End Page';
      default:
        return 'Page';
    }
  };

  return (
    <div className="w-64 bg-white border-r border-gray-200 flex flex-col">
      {/* Navigation */}
      <div className="p-4 border-b border-gray-200">
        <div className="space-y-1">
          <Button
            variant={activeSection === 'settings' ? 'default' : 'ghost'}
            className="w-full justify-start"
            onClick={() => onSectionChange('settings')}
          >
            <Settings className="w-4 h-4 mr-2" />
            Book Settings
          </Button>

          <Button
            variant={activeSection === 'pages' ? 'default' : 'ghost'}
            className="w-full justify-start"
            onClick={() => onSectionChange('pages')}
          >
            <FileText className="w-4 h-4 mr-2" />
            Pages
          </Button>

          <Button
            variant={activeSection === 'preview' ? 'default' : 'ghost'}
            className="w-full justify-start"
            onClick={() => onSectionChange('preview')}
          >
            <Eye className="w-4 h-4 mr-2" />
            Preview
          </Button>
        </div>
      </div>

      {/* Pages List */}
      {activeSection === 'pages' && (
        <div className="flex-1 overflow-y-auto">
          <div className="p-4">
            <div className="flex items-center justify-between mb-3">
              <button
                onClick={() => setIsPagesExpanded(!isPagesExpanded)}
                className="flex items-center space-x-1 text-sm font-medium text-gray-700 hover:text-gray-900"
              >
                {isPagesExpanded ? (
                  <ChevronDown className="w-4 h-4" />
                ) : (
                  <ChevronRight className="w-4 h-4" />
                )}
                <span>Pages ({pages.length})</span>
              </button>

              <Button
                size="sm"
                variant="outline"
                onClick={() => {
                  // Add new page logic
                  const newPage = {
                    page_number: pages.length + 1,
                    page_type: 'content',
                    title: `Page ${pages.length + 1}`,
                    content: '',
                    images: [],
                    layout: 'standard'
                  };
                  // This would need to be passed up to parent component
                }}
              >
                <Plus className="w-3 h-3" />
              </Button>
            </div>

            {isPagesExpanded && (
              <div className="space-y-1">
                {pages.map((page, index) => (
                  <div
                    key={index}
                    className={`
                      flex items-center justify-between p-2 rounded-md cursor-pointer transition-colors
                      ${selectedPageIndex === index 
                        ? 'bg-blue-50 border border-blue-200' 
                        : 'hover:bg-gray-50 border border-transparent'
                      }
                    `}
                    onClick={() => onPageSelect(index)}
                  >
                    <div className="flex items-center space-x-2 flex-1 min-w-0">
                      {getPageIcon(page.page_type)}
                      <div className="flex-1 min-w-0">
                        <div className="text-sm font-medium text-gray-900 truncate">
                          {page.title || `Page ${page.page_number}`}
                        </div>
                        <div className="text-xs text-gray-500">
                          {getPageTypeLabel(page.page_type)}
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center space-x-1">
                      {page.images && page.images.length > 0 && (
                        <Image className="w-3 h-3 text-gray-400" />
                      )}
                      <span className="text-xs text-gray-400">
                        {page.page_number}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Quick Actions */}
      <div className="p-4 border-t border-gray-200">
        <div className="space-y-2">
          <Button variant="outline" size="sm" className="w-full justify-start">
            <Plus className="w-4 h-4 mr-2" />
            Add Page
          </Button>

          <Button variant="outline" size="sm" className="w-full justify-start">
            <Image className="w-4 h-4 mr-2" />
            Add Images
          </Button>
        </div>
      </div>
    </div>
  );
}

components/book-builder/BookPreview.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  ZoomIn, 
  ZoomOut, 
  RotateCcw, 
  ChevronLeft, 
  ChevronRight,
  Maximize,
  Download,
  Eye
} from "lucide-react";

interface BookPreviewProps {
  bookData: any;
  currentPageIndex: number;
  themes: any[];
}

export function BookPreview({ bookData, currentPageIndex, themes }: BookPreviewProps) {
  const [zoom, setZoom] = useState(100);
  const [viewMode, setViewMode] = useState<'single' | 'spread'>('single');
  const [previewPage, setPreviewPage] = useState(currentPageIndex);

  useEffect(() => {
    setPreviewPage(currentPageIndex);
  }, [currentPageIndex]);

  const currentTheme = themes.find(t => t.id === bookData.theme_id);
  const coverTheme = themes.find(t => t.id === bookData.cover_theme_id);
  const page = bookData.pages?.[previewPage];

  const getThemeStyles = (pageType: string, themeOverride?: any) => {
    let theme = currentTheme;

    if (pageType === 'cover' && coverTheme) {
      theme = coverTheme;
    }

    if (themeOverride?.theme_id) {
      theme = themes.find(t => t.id === themeOverride.theme_id) || theme;
    }

    if (!theme?.template_data) {
      return {
        backgroundColor: '#ffffff',
        color: '#000000',
        fontFamily: 'serif'
      };
    }

    const data = theme.template_data;
    return {
      backgroundColor: data.colors?.background || '#ffffff',
      color: data.colors?.text || data.colors?.primary || '#000000',
      fontFamily: data.fonts?.body || 'serif'
    };
  };

  const renderPageContent = (pageData: any, index: number) => {
    if (!pageData) return null;

    const styles = getThemeStyles(pageData.page_type, pageData.theme_override);
    const isEvenPage = (index + 1) % 2 === 0;

    return (
      <div
        className={`
          relative bg-white shadow-lg mx-auto transition-all duration-300
          ${viewMode === 'spread' && bookData.pages.length > 1 ? 'w-1/2' : 'w-full max-w-2xl'}
        `}
        style={{
          aspectRatio: getAspectRatio(bookData.dimensions),
          transform: `scale(${zoom / 100})`,
          transformOrigin: 'center top'
        }}
      >
        {/* Page Background */}
        <div
          className="absolute inset-0 p-8 overflow-hidden"
          style={{
            backgroundColor: styles.backgroundColor,
            color: styles.color,
            fontFamily: styles.fontFamily
          }}
        >
          {/* Cover Page */}
          {pageData.page_type === 'cover' && (
            <div className="h-full flex flex-col justify-center items-center text-center space-y-6">
              {/* Cover Image */}
              {pageData.images && pageData.images[0] && (
                <div className="w-full h-1/2 mb-4">
                  <img
                    src={pageData.images[0]}
                    alt="Cover"
                    className="w-full h-full object-cover rounded-lg"
                  />
                </div>
              )}

              {/* Title */}
              <h1 className="text-4xl font-bold leading-tight">
                {bookData.title || 'Book Title'}
              </h1>

              {/* Subtitle */}
              {bookData.subtitle && (
                <h2 className="text-2xl font-light opacity-80">
                  {bookData.subtitle}
                </h2>
              )}

              {/* Author */}
              <p className="text-xl font-medium mt-auto">
                by {bookData.author_name || 'Author Name'}
              </p>
            </div>
          )}

          {/* Content Page */}
          {pageData.page_type === 'content' && (
            <div className="h-full">
              {/* Page Title */}
              {pageData.title && (
                <h2 className="text-2xl font-bold mb-6 text-center">
                  {pageData.title}
                </h2>
              )}

              {/* Layout: Image Top */}
              {pageData.layout === 'image-top' && pageData.images?.[0] && (
                <div className="mb-6">
                  <img
                    src={pageData.images[0]}
                    alt="Page illustration"
                    className="w-full h-48 object-cover rounded-lg"
                  />
                </div>
              )}

              {/* Layout: Two Column */}
              {pageData.layout === 'two-column' ? (
                <div className="grid grid-cols-2 gap-6 h-5/6">
                  <div className="space-y-4">
                    <div className="text-base leading-relaxed whitespace-pre-wrap">
                      {pageData.content?.split('\n\n').slice(0, Math.ceil(pageData.content.split('\n\n').length / 2)).join('\n\n')}
                    </div>
                  </div>
                  <div className="space-y-4">
                    <div className="text-base leading-relaxed whitespace-pre-wrap">
                      {pageData.content?.split('\n\n').slice(Math.ceil(pageData.content.split('\n\n').length / 2)).join('\n\n')}
                    </div>
                  </div>
                </div>
              ) : pageData.layout === 'image-left' ? (
                <div className="grid grid-cols-2 gap-6 h-5/6">
                  <div>
                    {pageData.images?.[0] && (
                      <img
                        src={pageData.images[0]}
                        alt="Page illustration"
                        className="w-full h-full object-cover rounded-lg"
                      />
                    )}
                  </div>
                  <div className="text-base leading-relaxed whitespace-pre-wrap">
                    {pageData.content}
                  </div>
                </div>
              ) : pageData.layout === 'image-right' ? (
                <div className="grid grid-cols-2 gap-6 h-5/6">
                  <div className="text-base leading-relaxed whitespace-pre-wrap">
                    {pageData.content}
                  </div>
                  <div>
                    {pageData.images?.[0] && (
                      <img
                        src={pageData.images[0]}
                        alt="Page illustration"
                        className="w-full h-full object-cover rounded-lg"
                      />
                    )}
                  </div>
                </div>
              ) : (
                /* Standard Layout */
                <div className="h-5/6">
                  <div className="text-base leading-relaxed whitespace-pre-wrap h-full overflow-hidden">
                    {pageData.content}
                  </div>
                </div>
              )}

              {/* Layout: Image Bottom */}
              {pageData.layout === 'image-bottom' && pageData.images?.[0] && (
                <div className="mt-6">
                  <img
                    src={pageData.images[0]}
                    alt="Page illustration"
                    className="w-full h-48 object-cover rounded-lg"
                  />
                </div>
              )}

              {/* Page Number */}
              <div className={`absolute bottom-4 text-sm opacity-60 ${isEvenPage ? 'left-8' : 'right-8'}`}>
                {pageData.page_number}
              </div>
            </div>
          )}

          {/* End Page */}
          {pageData.page_type === 'end' && (
            <div className="h-full flex flex-col justify-between">
              {/* Content */}
              <div className="flex-1 flex flex-col justify-center text-center space-y-6">
                <h2 className="text-3xl font-bold">{pageData.title || 'The End'}</h2>
                <div className="text-lg leading-relaxed whitespace-pre-wrap">
                  {pageData.content}
                </div>
              </div>

              {/* Book Info */}
              <div className="space-y-3 text-sm text-center opacity-75">
                <div>{bookData.title}</div>
                <div>by {bookData.author_name}</div>
                {bookData.isbn && (
                  <div>ISBN: {bookData.isbn}</div>
                )}
                {bookData.metadata?.publisher && (
                  <div>{bookData.metadata.publisher}</div>
                )}
                {bookData.metadata?.copyright && (
                  <div>© {bookData.metadata.copyright}</div>
                )}
              </div>

              {/* Barcode Area */}
              {bookData.metadata?.include_barcode && bookData.isbn && (
                <div className="mt-4 flex justify-center">
                  <div className="bg-black text-white px-4 py-2 text-xs font-mono">
                    ||| ||| ||| {bookData.isbn} ||| ||| |||
                  </div>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Page Border */}
        <div className="absolute inset-0 border border-gray-300 pointer-events-none"></div>
      </div>
    );
  };

  const getAspectRatio = (dimensions: string) => {
    switch (dimensions) {
      case 'A4': return '210/297';
      case 'Letter': return '8.5/11';
      case '6x9': return '6/9';
      case '8.5x11': return '8.5/11';
      case '5x8': return '5/8';
      case '7x10': return '7/10';
      default: return '210/297';
    }
  };

  const nextPage = () => {
    if (previewPage < bookData.pages.length - 1) {
      setPreviewPage(previewPage + 1);
    }
  };

  const prevPage = () => {
    if (previewPage > 0) {
      setPreviewPage(previewPage - 1);
    }
  };

  return (
    <div className="h-full flex flex-col bg-gray-100">
      {/* Preview Header */}
      <div className="p-4 bg-white border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <h3 className="font-semibold">Preview</h3>
            <Badge variant="outline">
              {bookData.dimensions || 'A4'}
            </Badge>
            <Badge variant="outline">
              Page {previewPage + 1} of {bookData.pages?.length || 0}
            </Badge>
          </div>

          <div className="flex items-center space-x-2">
            {/* Zoom Controls */}
            <Button
              size="sm"
              variant="outline"
              onClick={() => setZoom(Math.max(25, zoom - 25))}
              disabled={zoom <= 25}
            >
              <ZoomOut className="w-4 h-4" />
            </Button>
            <span className="text-sm font-medium w-12 text-center">{zoom}%</span>
            <Button
              size="sm"
              variant="outline"
              onClick={() => setZoom(Math.min(200, zoom + 25))}
              disabled={zoom >= 200}
            >
              <ZoomIn className="w-4 h-4" />
            </Button>
            <Button
              size="sm"
              variant="outline"
              onClick={() => setZoom(100)}
            >
              <RotateCcw className="w-4 h-4" />
            </Button>

            {/* View Mode */}
            <Button
              size="sm"
              variant={viewMode === 'single' ? 'default' : 'outline'}
              onClick={() => setViewMode('single')}
            >
              Single
            </Button>
            <Button
              size="sm"
              variant={viewMode === 'spread' ? 'default' : 'outline'}
              onClick={() => setViewMode('spread')}
            >
              Spread
            </Button>
          </div>
        </div>
      </div>

      {/* Preview Content */}
      <div className="flex-1 overflow-auto p-8">
        <div className="min-h-full flex items-start justify-center">
          {viewMode === 'single' ? (
            renderPageContent(page, previewPage)
          ) : (
            <div className="flex space-x-4 max-w-6xl">
              {/* Left Page */}
              {previewPage > 0 && renderPageContent(bookData.pages[previewPage - 1], previewPage - 1)}
              {/* Right Page */}
              {renderPageContent(page, previewPage)}
            </div>
          )}
        </div>
      </div>

      {/* Navigation Footer */}
      <div className="p-4 bg-white border-t border-gray-200">
        <div className="flex items-center justify-center space-x-4">
          <Button
            size="sm"
            variant="outline"
            onClick={prevPage}
            disabled={previewPage === 0}
          >
            <ChevronLeft className="w-4 h-4 mr-1" />
            Previous
          </Button>

          <div className="flex items-center space-x-2">
            {bookData.pages?.map((_, index) => (
              <button
                key={index}
                onClick={() => setPreviewPage(index)}
                className={`
                  w-3 h-3 rounded-full transition-all
                  ${previewPage === index 
                    ? 'bg-blue-500' 
                    : 'bg-gray-300 hover:bg-gray-400'
                  }
                `}
              />
            ))}
          </div>

          <Button
            size="sm"
            variant="outline"
            onClick={nextPage}
            disabled={previewPage === bookData.pages.length - 1}
          >
            Next
            <ChevronRight className="w-4 h-4 ml-1" />
          </Button>
        </div>
      </div>
    </div>
  );
}

components/book-builder/BookSettingsPane.content:
"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Book, 
  Palette, 
  Ruler, 
  User, 
  Hash,
  ImageIcon,
  Settings,
  Wand2
} from "lucide-react";

interface BookSettingsPanelProps {
  bookData: any;
  onBookDataChange: (data: any) => void;
  themes: any[];
}

export function BookSettingsPanel({ bookData, onBookDataChange, themes }: BookSettingsPanelProps) {
  const [activeTab, setActiveTab] = useState<'basic' | 'design' | 'advanced'>('basic');

  const dimensionOptions = [
    { value: 'A4', label: 'A4 (210 × 297 mm)', description: 'Standard book size' },
    { value: 'Letter', label: 'Letter (8.5 × 11 in)', description: 'US standard' },
    { value: '6x9', label: '6" × 9"', description: 'Popular novel size' },
    { value: '8.5x11', label: '8.5" × 11"', description: 'Large format' },
    { value: '5x8', label: '5" × 8"', description: 'Compact book' },
    { value: '7x10', label: '7" × 10"', description: 'Medium format' }
  ];

  const coverThemes = themes.filter(theme => 
    theme.category === 'cover' || theme.category === 'complete'
  );

  const pageThemes = themes.filter(theme => 
    theme.category === 'page' || theme.category === 'complete'
  );

  const handleInputChange = (field: string, value: any) => {
    onBookDataChange({
      ...bookData,
      [field]: value
    });
  };

  const generateISBN = () => {
    // Generate a mock ISBN for demonstration
    const isbn = `978-${Math.floor(Math.random() * 10000000000)}`;
    handleInputChange('isbn', isbn);
  };

  return (
    <div className="h-full overflow-y-auto">
      {/* Tab Navigation */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex space-x-1">
          <Button
            variant={activeTab === 'basic' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setActiveTab('basic')}
            className="flex items-center space-x-1"
          >
            <Book className="w-4 h-4" />
            <span>Basic</span>
          </Button>
          <Button
            variant={activeTab === 'design' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setActiveTab('design')}
            className="flex items-center space-x-1"
          >
            <Palette className="w-4 h-4" />
            <span>Design</span>
          </Button>
          <Button
            variant={activeTab === 'advanced' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setActiveTab('advanced')}
            className="flex items-center space-x-1"
          >
            <Settings className="w-4 h-4" />
            <span>Advanced</span>
          </Button>
        </div>
      </div>

      <div className="p-4 space-y-6">
        {/* Basic Settings */}
        {activeTab === 'basic' && (
          <div className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2 text-lg">
                  <Book className="w-5 h-5" />
                  <span>Book Information</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="title">Book Title *</Label>
                  <Input
                    id="title"
                    value={bookData.title}
                    onChange={(e) => handleInputChange('title', e.target.value)}
                    placeholder="Enter your book title"
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label htmlFor="subtitle">Subtitle</Label>
                  <Input
                    id="subtitle"
                    value={bookData.subtitle}
                    onChange={(e) => handleInputChange('subtitle', e.target.value)}
                    placeholder="Optional subtitle"
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label htmlFor="author">Author Name *</Label>
                  <Input
                    id="author"
                    value={bookData.author_name}
                    onChange={(e) => handleInputChange('author_name', e.target.value)}
                    placeholder="Author name"
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label htmlFor="isbn" className="flex items-center space-x-2">
                    <span>ISBN</span>
                    <Button
                      type="button"
                      variant="outline"
                      size="sm"
                      onClick={generateISBN}
                      className="ml-2"
                    >
                      <Wand2 className="w-3 h-3 mr-1" />
                      Generate
                    </Button>
                  </Label>
                  <Input
                    id="isbn"
                    value={bookData.isbn}
                    onChange={(e) => handleInputChange('isbn', e.target.value)}
                    placeholder="ISBN number"
                    className="mt-1"
                  />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2 text-lg">
                  <Ruler className="w-5 h-5" />
                  <span>Book Dimensions</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div>
                  <Label>Page Size</Label>
                  <Select 
                    value={bookData.dimensions} 
                    onValueChange={(value) => handleInputChange('dimensions', value)}
                  >
                    <SelectTrigger className="mt-1">
                      <SelectValue placeholder="Select page size" />
                    </SelectTrigger>
                    <SelectContent>
                      {dimensionOptions.map((option) => (
                        <SelectItem key={option.value} value={option.value}>
                          <div>
                            <div className="font-medium">{option.label}</div>
                            <div className="text-sm text-gray-500">{option.description}</div>
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </CardContent>
            </Card>
          </div>
        )}

        {/* Design Settings */}
        {activeTab === 'design' && (
          <div className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2 text-lg">
                  <ImageIcon className="w-5 h-5" />
                  <span>Cover Theme</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 gap-3">
                  {coverThemes.map((theme) => (
                    <div
                      key={theme.id}
                      className={`
                        p-3 border rounded-lg cursor-pointer transition-all
                        ${bookData.cover_theme_id === theme.id
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-gray-300'
                        }
                      `}
                      onClick={() => handleInputChange('cover_theme_id', theme.id)}
                    >
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="font-medium">{theme.display_name}</div>
                          <div className="text-sm text-gray-500">{theme.category}</div>
                        </div>
                        {theme.is_premium && (
                          <Badge variant="secondary">Premium</Badge>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2 text-lg">
                  <Palette className="w-5 h-5" />
                  <span>Page Theme</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 gap-3">
                  {pageThemes.map((theme) => (
                    <div
                      key={theme.id}
                      className={`
                        p-3 border rounded-lg cursor-pointer transition-all
                        ${bookData.theme_id === theme.id
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-gray-300'
                        }
                      `}
                      onClick={() => handleInputChange('theme_id', theme.id)}
                    >
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="font-medium">{theme.display_name}</div>
                          <div className="text-sm text-gray-500">{theme.category}</div>
                        </div>
                        {theme.is_premium && (
                          <Badge variant="secondary">Premium</Badge>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          </div>
        )}

        {/* Advanced Settings */}
        {activeTab === 'advanced' && (
          <div className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2 text-lg">
                  <Settings className="w-5 h-5" />
                  <span>Publishing Options</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="publisher">Publisher</Label>
                  <Input
                    id="publisher"
                    value={bookData.metadata?.publisher || ''}
                    onChange={(e) => handleInputChange('metadata', {
                      ...bookData.metadata,
                      publisher: e.target.value
                    })}
                    placeholder="Publisher name"
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label htmlFor="copyright">Copyright Year</Label>
                  <Input
                    id="copyright"
                    value={bookData.metadata?.copyright || new Date().getFullYear()}
                    onChange={(e) => handleInputChange('metadata', {
                      ...bookData.metadata,
                      copyright: e.target.value
                    })}
                    placeholder="2024"
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label htmlFor="description">Description</Label>
                  <Textarea
                    id="description"
                    value={bookData.metadata?.description || ''}
                    onChange={(e) => handleInputChange('metadata', {
                      ...bookData.metadata,
                      description: e.target.value
                    })}
                    placeholder="Book description for back cover"
                    rows={3}
                    className="mt-1"
                  />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2 text-lg">
                  <Hash className="w-5 h-5" />
                  <span>Barcode & ISBN</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    id="includeBarcode"
                    checked={bookData.metadata?.include_barcode || true}
                    onChange={(e) => handleInputChange('metadata', {
                      ...bookData.metadata,
                      include_barcode: e.target.checked
                    })}
                  />
                  <Label htmlFor="includeBarcode">Include barcode on back cover</Label>
                </div>

                <div className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    id="includePrice"
                    checked={bookData.metadata?.include_price || false}
                    onChange={(e) => handleInputChange('metadata', {
                      ...bookData.metadata,
                      include_price: e.target.checked
                    })}
                  />
                  <Label htmlFor="includePrice">Include price on cover</Label>
                </div>

                {bookData.metadata?.include_price && (
                  <div>
                    <Label htmlFor="price">Price</Label>
                    <Input
                      id="price"
                      value={bookData.metadata?.price || ''}
                      onChange={(e) => handleInputChange('metadata', {
                        ...bookData.metadata,
                        price: e.target.value
                      })}
                      placeholder="$9.99"
                      className="mt-1"
                    />
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        )}
      </div>
    </div>
  );
}

components/book-builder/EnhancedBookPrev.content:
"use client";

import { useState, useRef, useEffect, useMemo } from "react";
import { ZoomIn, ZoomOut, ZoomInIcon, ZoomOutIcon, Maximize2, Minimize2, BookOpen } from "lucide-react";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { BookOpenCheck, BookText, Bookmark, FileText, Image as ImageIcon, List } from "lucide-react";
import { BookPage, BookTheme, PageContent } from "@/types/book";

// Define a type for content blocks
type ContentBlock = {
  type: string;
  content?: string;
  url?: string;
  alt?: string;
  position?: string;
  width?: number;
  height?: number;
};

// Extend the PageContent type to include style and showPageNumber
interface ExtendedPageContent extends PageContent {
  style?: Record<string, any>;
  showPageNumber?: boolean;
}

interface EnhancedBookPreviewProps {
  book: {
    title: string;
    pages: BookPage[];
    theme_id?: string;
    theme?: any; // Theme object from the parent
  };
  currentPageIndex: number;
  onPageChange: (index: number) => void;
  scale?: number;
  className?: string;
}

const getPageIcon = (pageType: string) => {
  switch (pageType) {
    case 'cover':
      return <BookOpen className="w-4 h-4" />;
    case 'title':
      return <FileText className="w-4 h-4" />;
    case 'toc':
      return <List className="w-4 h-4" />;
    case 'chapter':
      return <Bookmark className="w-4 h-4" />;
    case 'content':
      return <BookText className="w-4 h-4" />;
    case 'image':
      return <ImageIcon className="w-4 h-4" />;
    default:
      return <FileText className="w-4 h-4" />;
  }
};

export function EnhancedBookPreview({
  book,
  currentPageIndex,
  onPageChange,
  scale: initialScale = 1,
  className = "",
}: EnhancedBookPreviewProps) {
  const [scale, setScale] = useState(initialScale);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [showThumbnails, setShowThumbnails] = useState(true);
  const previewRef = useRef<HTMLDivElement>(null);
  const currentPage = book.pages[currentPageIndex] || book.pages[0];
  // Get the content from the current page, ensuring it's properly typed
  const content = useMemo<ExtendedPageContent>(() => {
    const now = new Date().toISOString();
    const defaultContent: ExtendedPageContent = {
      blocks: [],
      version: 1,
      lastModified: now,
      lastModifiedBy: 'system',
      style: {},
      showPageNumber: true
    };

    if (!currentPage?.content) return defaultContent;

    if (typeof currentPage.content === 'string') {
      return {
        ...defaultContent,
        rawHtml: currentPage.content,
        markdown: currentPage.content,
      };
    }

    // Ensure we have a valid PageContent object
    return {
      ...defaultContent,
      ...currentPage.content,
      blocks: Array.isArray(currentPage.content.blocks) ? currentPage.content.blocks : [],
    };
  }, [currentPage]);

  // Handle keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'ArrowRight' || e.key === ' ') {
        // Next page
        e.preventDefault();
        if (currentPageIndex < book.pages.length - 1) {
          onPageChange(currentPageIndex + 1);
        }
      } else if (e.key === 'ArrowLeft') {
        // Previous page
        e.preventDefault();
        if (currentPageIndex > 0) {
          onPageChange(currentPageIndex - 1);
        }
      } else if (e.key === 'f' || e.key === 'F') {
        // Toggle fullscreen
        e.preventDefault();
        setIsFullscreen(!isFullscreen);
      } else if (e.key === 't' || e.key === 'T') {
        // Toggle thumbnails
        e.preventDefault();
        setShowThumbnails(!showThumbnails);
      } else if (e.key === '+' || e.key === '=') {
        // Zoom in
        e.preventDefault();
        setScale(prev => Math.min(prev + 0.1, 2));
      } else if (e.key === '-' || e.key === '_') {
        // Zoom out
        e.preventDefault();
        setScale(prev => Math.max(prev - 0.1, 0.5));
      } else if (e.key === '0' || e.key === ')') {
        // Reset zoom
        e.preventDefault();
        setScale(1);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [currentPageIndex, book.pages.length, onPageChange, isFullscreen, showThumbnails]);

  // Toggle fullscreen
  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      previewRef.current?.requestFullscreen().catch(err => {
        console.error(`Error attempting to enable fullscreen: ${err.message}`);
      });
      setIsFullscreen(true);
    } else {
      document.exitFullscreen();
      setIsFullscreen(false);
    }
  };



  // Render page content based on type
  const renderPageContent = () => {
    if (!content) return null;

    // Get page style from content or use defaults
    const pageStyle: React.CSSProperties = {
      fontFamily: 'Arial, sans-serif',
      fontSize: '16px',
      lineHeight: 1.5,
      textAlign: 'left',
      color: '#333',
      backgroundColor: '#fff',
      padding: '2rem',
      height: '100%',
      overflow: 'auto',
      display: 'flex',
      flexDirection: 'column',
    };

    switch (currentPage.type) {
      case 'cover':
        return (
          <div 
            className="w-full h-full flex flex-col items-center justify-center p-8 text-center"
            style={{
              background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
              ...pageStyle
            }}
          >
            <h1 className="text-4xl font-bold mb-4">{book.title}</h1>
            {content.blocks.length > 0 && (
              <div className="text-xl mt-4 max-w-2xl">
                {content.blocks.map((block, i) => (
                  <p key={i}>{block.content}</p>
                ))}
              </div>
            )}
          </div>
        );

      case 'toc':
        return (
          <div className="w-full h-full p-8" style={pageStyle}>
            <h1 className="text-2xl font-bold mb-6">Table of Contents</h1>
            <div className="space-y-2">
              {book.pages
                .filter(page => page.type !== 'cover' && page.type !== 'toc' && page.type !== 'title')
                .map((page, index) => (
                  <div key={page.id} className="flex justify-between items-center">
                    <span className="truncate">{page.title || `Page ${page.pageNumber}`}</span>
                    <span className="ml-4 text-muted-foreground">{page.pageNumber}</span>
                  </div>
                ))}
            </div>
          </div>
        );

      default:
        // For regular content pages, render the content based on what's available
        return (
          <div className="w-full h-full p-8" style={pageStyle}>
            {content.rawHtml ? (
              <div 
                className="prose max-w-none"
                dangerouslySetInnerHTML={{ __html: content.rawHtml }} 
              />
            ) : content.markdown ? (
              <div className="prose max-w-none">
                {content.markdown.split('\n').map((line, i) => (
                  <p key={i}>{line}</p>
                ))}
              </div>
            ) : content.blocks && content.blocks.length > 0 ? (
              <div className="prose max-w-none">
                {(content.blocks as ContentBlock[]).map((block, i: number) => (
                  <div key={i} className="mb-4">
                    {block.type === 'heading' && (
                      <h2 className="text-2xl font-bold">{block.content}</h2>
                    )}
                    {block.type === 'paragraph' && (
                      <p>{block.content}</p>
                    )}
                    {block.type === 'image' && (
                      <div className="my-4">
                        <img 
                          src={block.url} 
                          alt={block.alt || ''} 
                          className="max-w-full h-auto rounded"
                        />
                        {block.alt && (
                          <p className="text-sm text-center text-muted-foreground mt-1">
                            {block.alt}
                          </p>
                        )}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-muted-foreground">No content available</p>
            )}
          </div>
        );
    }
  };

  if (!currentPage) return null;

  return (
    <div 
      ref={previewRef}
      className={cn(
        "flex flex-col h-full bg-gray-100 relative",
        isFullscreen ? 'fixed inset-0 z-50 bg-white' : 'relative',
        className
      )}
    >
      {/* Toolbar */}
      <div className="bg-white border-b border-gray-200 p-2 flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Button 
            variant="ghost" 
            size="sm" 
            onClick={() => setScale(prev => Math.max(prev - 0.1, 0.5))}
            title="Zoom Out"
          >
            <ZoomOutIcon className="h-4 w-4" />
          </Button>

          <span className="text-sm text-muted-foreground w-16 text-center">
            {Math.round(scale * 100)}%
          </span>

          <Button 
            variant="ghost" 
            size="sm" 
            onClick={() => setScale(prev => Math.min(prev + 0.1, 2))}
            title="Zoom In"
          >
            <ZoomInIcon className="h-4 w-4" />
          </Button>

          <Button 
            variant="ghost" 
            size="sm" 
            onClick={() => setScale(1)}
            className="text-xs"
          >
            Reset
          </Button>

          <div className="h-6 w-px bg-gray-200 mx-2"></div>

          <Button 
            variant="ghost" 
            size="sm" 
            onClick={toggleFullscreen}
            title={isFullscreen ? 'Exit Fullscreen' : 'Enter Fullscreen'}
          >
            {isFullscreen ? (
              <Minimize2 className="h-4 w-4" />
            ) : (
              <Maximize2 className="h-4 w-4" />
            )}
          </Button>

          <Button 
            variant="ghost" 
            size="sm" 
            onClick={() => setShowThumbnails(!showThumbnails)}
            title="Toggle Thumbnails"
          >
            <BookOpen className="h-4 w-4" />
          </Button>
        </div>

        <div className="flex items-center space-x-2">
          <span className="text-sm text-muted-foreground">
            Page {currentPageIndex + 1} of {book.pages.length}
          </span>
        </div>
      </div>

      <div className="flex-1 flex overflow-hidden">
        {/* Thumbnails sidebar */}
        {showThumbnails && (
          <div className="w-48 border-r border-gray-200 bg-white overflow-y-auto p-2">
            <div className="space-y-2">
              {book.pages.map((page, index) => (
                <div 
                  key={page.id}
                  className={cn(
                    "p-2 rounded border cursor-pointer flex items-center space-x-2 text-sm",
                    currentPageIndex === index 
                      ? 'border-blue-500 bg-blue-50' 
                      : 'border-transparent hover:bg-gray-50'
                  )}
                  onClick={() => onPageChange(index)}
                >
                  <div className="text-muted-foreground">
                    {getPageIcon(page.type)}
                  </div>
                  <span className="truncate">
                    {page.title || `${page.type.charAt(0).toUpperCase() + page.type.slice(1)} ${index + 1}`}
                  </span>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Main preview */}
        <div className="flex-1 flex items-center justify-center p-8 overflow-auto">
          <div 
            className="bg-white shadow-lg"
            style={{
              transform: `scale(${scale})`,
              transformOrigin: 'center',
              width: '210mm', // A4 width
              minHeight: '297mm', // A4 height
              transition: 'transform 0.2s ease-in-out',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
              position: 'relative',
              overflow: 'hidden'
            }}
          >
            {renderPageContent()}

            {/* Page number */}
            {content.showPageNumber !== false && (
              <div 
                className="absolute bottom-4 right-4 text-xs text-gray-500"
              >
                Page {currentPageIndex + 1} of {book.pages.length}
              </div>
            )}
          </div>
        </div>

        {/* Navigation arrows */}
        <div className="absolute inset-y-0 left-0 flex items-center pl-2">
          <Button
            variant="ghost"
            size="icon"
            className="rounded-full w-10 h-10 bg-white/80 backdrop-blur-sm hover:bg-white"
            onClick={() => currentPageIndex > 0 && onPageChange(currentPageIndex - 1)}
            disabled={currentPageIndex === 0}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="h-5 w-5"
            >
              <path d="m15 18-6-6 6-6" />
            </svg>
            <span className="sr-only">Previous page</span>
          </Button>
        </div>

        <div className="absolute inset-y-0 right-0 flex items-center pr-2">
          <Button
            variant="ghost"
            size="icon"
            className="rounded-full w-10 h-10 bg-white/80 backdrop-blur-sm hover:bg-white"
            onClick={() => currentPageIndex < book.pages.length - 1 && onPageChange(currentPageIndex + 1)}
            disabled={currentPageIndex === book.pages.length - 1}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="h-5 w-5"
            >
              <path d="m9 18 6-6-6-6" />
            </svg>
            <span className="sr-only">Next page</span>
          </Button>
        </div>
      </div>
    </div>
  );
}

components/book-builder/EnhancedPageEdit.content:
"use client";

import { useState, useEffect, useCallback } from "react";
import { Bold, Italic, Underline, List, ListOrdered, Image as ImageIcon, Link2, Quote, Code, Undo, Redo, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from '@radix-ui/react-slider'
import { cn } from "@/lib/utils";

type PageType = 'cover' | 'title' | 'copyright' | 'toc' | 'chapter' | 'content' | 'image' | 'default';

interface PageContent {
  title?: string;
  content?: string;
  style?: {
    fontFamily?: string;
    fontSize?: string;
    lineHeight?: number;
    textAlign?: 'left' | 'center' | 'right' | 'justify';
    color?: string;
    backgroundColor?: string;
  };
  images?: Array<{
    url: string;
    alt: string;
    width?: number;
    height?: number;
    position?: 'left' | 'right' | 'center' | 'full';
  }>;
  layout?: string;
  pageNumber?: number | string;
  showPageNumber?: boolean;
}

interface EnhancedPageEditorProps {
  page: {
    id: string;
    type: PageType;
    title: string;
    content: PageContent | string;
    pageNumber: number;
    layout?: string;
    style?: Record<string, any>;
  };
  onPageChange: (updatedPage: any) => void;
  themes?: Array<{ id: string; name: string; styles: Record<string, any> }>;
  className?: string;
}

export function EnhancedPageEditor({
  page,
  onPageChange,
  themes = [],
  className = "",
}: EnhancedPageEditorProps) {
  const [activeTab, setActiveTab] = useState('content');
  const [content, setContent] = useState<PageContent>(
    typeof page.content === 'string' 
      ? { content: page.content } 
      : page.content || {}
  );

  // Update local content when page prop changes
  useEffect(() => {
    setContent(
      typeof page.content === 'string' 
        ? { content: page.content } 
        : page.content || {}
    );
  }, [page.id, page.content]);

  const handleContentChange = useCallback((updates: Partial<PageContent>) => {
    setContent(prev => {
      const newContent = { ...prev, ...updates };

      // Notify parent of changes
      setTimeout(() => {
        onPageChange({
          ...page,
          content: newContent,
          title: newContent.title || page.title
        });
      }, 0);

      return newContent;
    });
  }, [onPageChange, page]);

  const handleStyleChange = (property: string, value: any) => {
    handleContentChange({
      style: {
        ...content.style,
        [property]: value
      }
    });
  };

  const handleAddImage = () => {
    // In a real app, this would open a file picker and upload the image
    const imageUrl = prompt('Enter image URL:');
    if (imageUrl) {
      const newImage = {
        url: imageUrl,
        alt: 'Image ' + (content.images?.length || 0 + 1),
        position: 'center' as const
      };

      handleContentChange({
        images: [...(content.images || []), newImage]
      });
    }
  };

  return (
    <div className={cn("h-full flex flex-col", className)}>
      <Tabs 
        value={activeTab} 
        onValueChange={setActiveTab}
        className="flex-1 flex flex-col"
      >
        <TabsList className="w-full justify-start rounded-none border-b bg-transparent p-0">
          <TabsTrigger 
            value="content" 
            className="relative rounded-none border-b-2 border-transparent bg-transparent px-4 pb-3 pt-2 font-semibold text-muted-foreground shadow-none transition-none data-[state=active]:border-primary data-[state=active]:text-foreground data-[state=active]:shadow-none"
          >
            Content
          </TabsTrigger>
          <TabsTrigger 
            value="style" 
            className="relative rounded-none border-b-2 border-transparent bg-transparent px-4 pb-3 pt-2 font-semibold text-muted-foreground shadow-none transition-none data-[state=active]:border-primary data-[state=active]:text-foreground data-[state=active]:shadow-none"
          >
            Style
          </TabsTrigger>
          <TabsTrigger 
            value="layout" 
            className="relative rounded-none border-b-2 border-transparent bg-transparent px-4 pb-3 pt-2 font-semibold text-muted-foreground shadow-none transition-none data-[state=active]:border-primary data-[state=active]:text-foreground data-[state=active]:shadow-none"
          >
            Layout
          </TabsTrigger>
        </TabsList>

        <div className="flex-1 overflow-y-auto p-4">
          <TabsContent value="content" className="m-0">
            <div className="space-y-4">
              <div>
                <Label htmlFor="page-title">Page Title</Label>
                <Input
                  id="page-title"
                  value={content.title || ''}
                  onChange={(e) => handleContentChange({ title: e.target.value })}
                  placeholder="Enter page title"
                  className="mt-1"
                />
              </div>

              <div>
                <div className="flex items-center justify-between mb-1">
                  <Label>Content</Label>
                  <div className="flex space-x-1">
                    <Button variant="ghost" size="icon" className="h-8 w-8">
                      <Bold className="h-4 w-4" />
                    </Button>
                    <Button variant="ghost" size="icon" className="h-8 w-8">
                      <Italic className="h-4 w-4" />
                    </Button>
                    <Button variant="ghost" size="icon" className="h-8 w-8">
                      <Underline className="h-4 w-4" />
                    </Button>
                    <Button variant="ghost" size="icon" className="h-8 w-8" onClick={handleAddImage}>
                      <ImageIcon className="h-4 w-4" />
                    </Button>
                    <Button variant="ghost" size="icon" className="h-8 w-8">
                      <List className="h-4 w-4" />
                    </Button>
                    <Button variant="ghost" size="icon" className="h-8 w-8">
                      <ListOrdered className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
                <Textarea
                  value={typeof content.content === 'string' ? content.content : ''}
                  onChange={(e) => handleContentChange({ content: e.target.value })}
                  placeholder="Type your content here..."
                  className="min-h-[200px] font-mono text-sm"
                />
              </div>

              {content.images && content.images.length > 0 && (
                <div className="space-y-2">
                  <Label>Images</Label>
                  <div className="space-y-2">
                    {content.images.map((img, index) => (
                      <div key={index} className="border rounded p-2 flex items-center space-x-2">
                        <div className="w-12 h-12 bg-gray-100 flex items-center justify-center rounded overflow-hidden">
                          <img 
                            src={img.url} 
                            alt={img.alt} 
                            className="max-w-full max-h-full object-contain"
                          />
                        </div>
                        <div className="flex-1 min-w-0">
                          <div className="text-sm font-medium truncate">{img.alt}</div>
                          <div className="text-xs text-muted-foreground truncate">{img.url}</div>
                        </div>
                        <Button variant="ghost" size="icon" className="h-8 w-8">
                          <Trash2 className="h-4 w-4 text-red-500" />
                        </Button>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </TabsContent>

          <TabsContent value="style" className="m-0">
            <div className="space-y-4">
              <div>
                <Label>Font Family</Label>
                <Select
                  value={content.style?.fontFamily || 'Arial, sans-serif'}
                  onValueChange={(value) => handleStyleChange('fontFamily', value)}
                >
                  <SelectTrigger className="w-full mt-1">
                    <SelectValue placeholder="Select font" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="Arial, sans-serif">Arial</SelectItem>
                    <SelectItem value="Georgia, serif">Georgia</SelectItem>
                    <SelectItem value="'Times New Roman', Times, serif">Times New Roman</SelectItem>
                    <SelectItem value="'Courier New', Courier, monospace">Courier New</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <div className="flex justify-between items-center">
                  <Label>Font Size: {content.style?.fontSize || '16px'}</Label>
                  <span className="text-sm text-muted-foreground">
                    {parseInt(content.style?.fontSize || '16')}px
                  </span>
                </div>
                <Slider
                  value={[parseInt(content.style?.fontSize || '16')]}
                  onValueChange={([value]) => handleStyleChange('fontSize', `${value}px`)}
                  min={8}
                  max={72}
                  step={1}
                  className="mt-2"
                />
              </div>

              <div>
                <div className="flex justify-between items-center">
                  <Label>Line Height: {content.style?.lineHeight || 1.5}</Label>
                  <span className="text-sm text-muted-foreground">
                    {content.style?.lineHeight || 1.5}
                  </span>
                </div>
                <Slider
                  value={[content.style?.lineHeight || 1.5]}
                  onValueChange={([value]) => handleStyleChange('lineHeight', value)}
                  min={1}
                  max={3}
                  step={0.1}
                  className="mt-2"
                />
              </div>

              <div>
                <Label>Text Alignment</Label>
                <div className="grid grid-cols-4 gap-2 mt-1">
                  {['left', 'center', 'right', 'justify'].map((align) => (
                    <Button
                      key={align}
                      variant={content.style?.textAlign === align ? 'default' : 'outline'}
                      size="sm"
                      className="capitalize"
                      onClick={() => handleStyleChange('textAlign', align as any)}
                    >
                      {align}
                    </Button>
                  ))}
                </div>
              </div>
            </div>
          </TabsContent>

          <TabsContent value="layout" className="m-0">
            <div className="space-y-4">
              <div>
                <Label>Page Layout</Label>
                <Select
                  value={page.layout || 'standard'}
                  onValueChange={(value) => onPageChange({ ...page, layout: value })}
                >
                  <SelectTrigger className="w-full mt-1">
                    <SelectValue placeholder="Select layout" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="standard">Standard</SelectItem>
                    <SelectItem value="full-width">Full Width</SelectItem>
                    <SelectItem value="sidebar">With Sidebar</SelectItem>
                    <SelectItem value="two-column">Two Columns</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="pt-2">
                <div className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    id="show-page-number"
                    checked={content.showPageNumber !== false}
                    onChange={(e) => handleContentChange({ showPageNumber: e.target.checked })}
                    className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <Label htmlFor="show-page-number" className="text-sm font-medium">
                    Show page number
                  </Label>
                </div>
              </div>

              {themes.length > 0 && (
                <div>
                  <Label>Theme</Label>
                  <Select
                    value={page.style?.themeId || themes[0]?.id}
                    onValueChange={(value) => 
                      onPageChange({ 
                        ...page, 
                        style: { 
                          ...page.style, 
                          themeId: value 
                        } 
                      })
                    }
                  >
                    <SelectTrigger className="w-full mt-1">
                      <SelectValue placeholder="Select theme" />
                    </SelectTrigger>
                    <SelectContent>
                      {themes.map((theme) => (
                        <SelectItem key={theme.id} value={theme.id}>
                          {theme.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              )}
            </div>
          </TabsContent>
        </div>
      </Tabs>
    </div>
  );
}

components/book-builder/EnhancedSidebar..content:
"use client";

import { useState, useCallback } from "react";
import { GripVertical, Plus, Trash2, FileText, Image as ImageIcon, Settings, Eye, List } from "lucide-react";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { DndContext, closestCenter, KeyboardSensor, PointerSensor, useSensor, useSensors } from "@dnd-kit/core";
import { arrayMove, SortableContext, sortableKeyboardCoordinates, verticalListSortingStrategy } from "@dnd-kit/sortable";
interface Page {
  id: string;
  title: string;
  type: string;
  pageNumber: number;
}

interface EnhancedSidebarProps {
  activeSection: string;
  onSectionChange: (section: string) => void;
  pages: Page[];
  selectedPageIndex: number;
  onPageSelect: (index: number) => void;
  onAddPage: (type: string) => void;
  onDeletePage: (index: number) => void;
  onPageReorder: (oldIndex: number, newIndex: number) => void;
}

const pageTypeIcons: Record<string, React.ReactNode> = {
  cover: <FileText className="w-4 h-4" />,
  title: <FileText className="w-4 h-4" />,
  copyright: <FileText className="w-4 h-4" />,
  toc: <List className="w-4 h-4" />,
  chapter: <FileText className="w-4 h-4" />,
  content: <FileText className="w-4 h-4" />,
  image: <ImageIcon className="w-4 h-4" />,
  default: <FileText className="w-4 h-4" />
};

export function EnhancedSidebar({
  activeSection,
  onSectionChange,
  pages,
  selectedPageIndex,
  onPageSelect,
  onAddPage,
  onDeletePage,
  onPageReorder
}: EnhancedSidebarProps) {
  const [isAddingPage, setIsAddingPage] = useState(false);
  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  const handleDragEnd = useCallback((event: any) => {
    const { active, over } = event;

    if (active.id !== over.id) {
      const oldIndex = pages.findIndex(page => page.id === active.id);
      const newIndex = pages.findIndex(page => page.id === over.id);
      onPageReorder(oldIndex, newIndex);
    }
  }, [pages, onPageReorder]);

  const navItems = [
    { id: 'pages', label: 'Pages', icon: <FileText className="w-4 h-4" /> },
    { id: 'settings', label: 'Settings', icon: <Settings className="w-4 h-4" /> },
    { id: 'preview', label: 'Preview', icon: <Eye className="w-4 h-4" /> }
  ];

  return (
    <div className="w-64 bg-white border-r border-gray-200 flex flex-col h-full">
      {/* Navigation Tabs */}
      <div className="flex border-b border-gray-200">
        {navItems.map((item) => (
          <button
            key={item.id}
            className={cn(
              "flex-1 py-3 px-4 text-sm font-medium flex items-center justify-center space-x-2",
              activeSection === item.id 
                ? "text-blue-600 border-b-2 border-blue-600" 
                : "text-gray-500 hover:text-gray-700"
            )}
            onClick={() => onSectionChange(item.id)}
          >
            {item.icon}
            <span>{item.label}</span>
          </button>
        ))}
      </div>

      {/* Page List */}
      <div className="flex-1 overflow-hidden flex flex-col">
        <div className="p-3 border-b border-gray-200">
          <div className="flex justify-between items-center mb-2">
            <h3 className="text-sm font-medium text-gray-700">Pages</h3>
            <div className="relative">
              <Button
                variant="ghost"
                size="sm"
                className="h-8 px-2"
                onClick={() => setIsAddingPage(!isAddingPage)}
              >
                <Plus className="w-4 h-4 mr-1" />
                <span>Add</span>
              </Button>

              {isAddingPage && (
                <div className="absolute right-0 mt-1 w-48 bg-white rounded-md shadow-lg z-10 border border-gray-200">
                  <div className="py-1">
                    <button
                      onClick={() => {
                        onAddPage('content');
                        setIsAddingPage(false);
                      }}
                      className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 flex items-center space-x-2"
                    >
                      <FileText className="w-4 h-4" />
                      <span>Text Page</span>
                    </button>
                    <button
                      onClick={() => {
                        onAddPage('image');
                        setIsAddingPage(false);
                      }}
                      className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 flex items-center space-x-2"
                    >
                      <ImageIcon className="w-4 h-4" />
                      <span>Image Page</span>
                    </button>
                    <div className="border-t border-gray-200 my-1"></div>
                    <button
                      onClick={() => {
                        onAddPage('cover');
                        setIsAddingPage(false);
                      }}
                      className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Cover Page
                    </button>
                    <button
                      onClick={() => {
                        onAddPage('toc');
                        setIsAddingPage(false);
                      }}
                      className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    >
                      Table of Contents
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="flex-1">
          <DndContext
            sensors={sensors}
            collisionDetection={closestCenter}
            onDragEnd={handleDragEnd}
          >
            <SortableContext
              items={pages.map(page => page.id)}
              strategy={verticalListSortingStrategy}
            >
              <div className="p-2 space-y-1">
                {pages.map((page, index) => (
                  <div key={page.id} id={page.id}>
                    <div
                      className={cn(
                        "group flex items-center justify-between p-2 rounded-md hover:bg-gray-50 cursor-pointer",
                        selectedPageIndex === index ? "bg-blue-50 border border-blue-200" : "border border-transparent"
                      )}
                      onClick={() => onPageSelect(index)}
                    >
                      <div className="flex items-center space-x-2 flex-1 min-w-0">
                        <GripVertical className="w-4 h-4 text-gray-400 flex-shrink-0" />
                        <div className="flex-shrink-0 text-xs text-gray-500 w-5">
                          {page.pageNumber}
                        </div>
                        <div className="truncate text-sm">
                          {page.title || `Page ${page.pageNumber}`}
                        </div>
                      </div>
                      <button
                        className="opacity-0 group-hover:opacity-100 text-gray-400 hover:text-red-500 p-1"
                        onClick={(e) => {
                          e.stopPropagation();
                          onDeletePage(index);
                        }}
                      >
                        <Trash2 className="w-3.5 h-3.5" />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </SortableContext>
          </DndContext>
        </div>
      </div>
    </div>
  );
}

components/book-builder/PageEditor.tsx.content:
"use client";

import { useState, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Upload, 
  Image, 
  Trash2, 
  Move, 
  Type, 
  Layout,
  Palette,
  Wand2,
  Plus,
  X
} from "lucide-react";
import { toast } from "sonner";

interface PageEditorProps {
  page: any;
  pageIndex: number;
  onPageChange: (page: any) => void;
  themes: any[];
}

export function PageEditor({ page, pageIndex, onPageChange, themes }: PageEditorProps) {
  const [activeTab, setActiveTab] = useState<'content' | 'layout' | 'images' | 'theme'>('content');
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const layoutOptions = [
    { value: 'standard', label: 'Standard', description: 'Basic single column layout' },
    { value: 'two-column', label: 'Two Column', description: 'Split content into two columns' },
    { value: 'image-left', label: 'Image Left', description: 'Image on left, text on right' },
    { value: 'image-right', label: 'Image Right', description: 'Image on right, text on left' },
    { value: 'image-top', label: 'Image Top', description: 'Image above text' },
    { value: 'image-bottom', label: 'Image Bottom', description: 'Image below text' },
    { value: 'full-image', label: 'Full Image', description: 'Full page image with overlay text' },
    { value: 'cover', label: 'Cover Layout', description: 'Cover page specific layout' },
    { value: 'end', label: 'End Page', description: 'Final page with credits and info' }
  ];

  const pageTypeOptions = [
    { value: 'content', label: 'Content Page', description: 'Regular story content' },
    { value: 'cover', label: 'Cover Page', description: 'Front cover of the book' },
    { value: 'end', label: 'End Page', description: 'Back matter with credits' }
  ];

  const handlePageUpdate = (field: string, value: any) => {
    onPageChange({
      ...page,
      [field]: value
    });
  };

  const handleImageUpload = async (files: FileList | null) => {
    if (!files || files.length === 0) return;

    setIsUploading(true);
    try {
      const uploadedImages = [];

      for (let i = 0; i < files.length; i++) {
        const file = files[i];

        // Create FormData for upload
        const formData = new FormData();
        formData.append('file', file);
        formData.append('type', 'book-page');

        const response = await fetch('/api/v1/media/upload', {
          method: 'POST',
          body: formData
        });

        if (!response.ok) throw new Error('Upload failed');

        const result = await response.json();
        uploadedImages.push(result.url);
      }

      // Add uploaded images to page
      const currentImages = page.images || [];
      handlePageUpdate('images', [...currentImages, ...uploadedImages]);

      toast.success(`${uploadedImages.length} image(s) uploaded successfully!`);

    } catch (error) {
      console.error('Upload error:', error);
      toast.error('Failed to upload images');
    } finally {
      setIsUploading(false);
    }
  };

  const handleGenerateImage = async (prompt: string) => {
    if (!prompt.trim()) {
      toast.error('Please enter a description for the image');
      return;
    }

    setIsUploading(true);
    try {
      const response = await fetch('/api/v1/media/generate-image', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          prompt,
          aspectRatio: '16:9',
          style: 'book-illustration'
        })
      });

      if (!response.ok) throw new Error('Generation failed');

      const result = await response.json();

      // Add generated image to page
      const currentImages = page.images || [];
      handlePageUpdate('images', [...currentImages, result.url]);

      toast.success('Image generated successfully!');

    } catch (error) {
      console.error('Generation error:', error);
      toast.error('Failed to generate image');
    } finally {
      setIsUploading(false);
    }
  };

  const removeImage = (index: number) => {
    const currentImages = page.images || [];
    const newImages = currentImages.filter((_, i) => i !== index);
    handlePageUpdate('images', newImages);
  };

  const moveImage = (index: number, direction: 'up' | 'down') => {
    const currentImages = page.images || [];
    const newImages = [...currentImages];

    if (direction === 'up' && index > 0) {
      [newImages[index], newImages[index - 1]] = [newImages[index - 1], newImages[index]];
    } else if (direction === 'down' && index < newImages.length - 1) {
      [newImages[index], newImages[index + 1]] = [newImages[index + 1], newImages[index]];
    }

    handlePageUpdate('images', newImages);
  };

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="font-semibold text-gray-900">
              Page {page.page_number}: {page.title || 'Untitled'}
            </h3>
            <p className="text-sm text-gray-500">
              {pageTypeOptions.find(opt => opt.value === page.page_type)?.label}
            </p>
          </div>
          <Badge variant="outline">
            {layoutOptions.find(opt => opt.value === page.layout)?.label}
          </Badge>
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="border-b border-gray-200">
        <div className="flex space-x-1 p-1">
          <Button
            variant={activeTab === 'content' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setActiveTab('content')}
            className="flex items-center space-x-1"
          >
            <Type className="w-4 h-4" />
            <span>Content</span>
          </Button>
          <Button
            variant={activeTab === 'layout' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setActiveTab('layout')}
            className="flex items-center space-x-1"
          >
            <Layout className="w-4 h-4" />
            <span>Layout</span>
          </Button>
          <Button
            variant={activeTab === 'images' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setActiveTab('images')}
            className="flex items-center space-x-1"
          >
            <Image className="w-4 h-4" />
            <span>Images</span>
          </Button>
          <Button
            variant={activeTab === 'theme' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setActiveTab('theme')}
            className="flex items-center space-x-1"
          >
            <Palette className="w-4 h-4" />
            <span>Theme</span>
          </Button>
        </div>
      </div>

      {/* Content */}
      <div className="flex-1 overflow-y-auto p-4 space-y-6">
        {/* Content Tab */}
        {activeTab === 'content' && (
          <div className="space-y-4">
            <div>
              <Label htmlFor="pageTitle">Page Title</Label>
              <Input
                id="pageTitle"
                value={page.title || ''}
                onChange={(e) => handlePageUpdate('title', e.target.value)}
                placeholder="Enter page title"
                className="mt-1"
              />
            </div>

            <div>
              <Label htmlFor="pageType">Page Type</Label>
              <Select 
                value={page.page_type} 
                onValueChange={(value) => handlePageUpdate('page_type', value)}
              >
                <SelectTrigger className="mt-1">
                  <SelectValue placeholder="Select page type" />
                </SelectTrigger>
                <SelectContent>
                  {pageTypeOptions.map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      <div>
                        <div className="font-medium">{option.label}</div>
                        <div className="text-sm text-gray-500">{option.description}</div>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="pageContent">Content</Label>
              <Textarea
                id="pageContent"
                value={page.content || ''}
                onChange={(e) => handlePageUpdate('content', e.target.value)}
                placeholder="Enter page content..."
                rows={12}
                className="mt-1 font-serif leading-relaxed"
              />
              <p className="text-sm text-gray-500 mt-1">
                {page.content ? page.content.length : 0} characters
              </p>
            </div>
          </div>
        )}

        {/* Layout Tab */}
        {activeTab === 'layout' && (
          <div className="space-y-4">
            <div>
              <Label>Layout Style</Label>
              <div className="grid grid-cols-1 gap-3 mt-2">
                {layoutOptions.map((option) => (
                  <div
                    key={option.value}
                    className={`
                      p-3 border rounded-lg cursor-pointer transition-all
                      ${page.layout === option.value
                        ? 'border-blue-500 bg-blue-50'
                        : 'border-gray-200 hover:border-gray-300'
                      }
                    `}
                    onClick={() => handlePageUpdate('layout', option.value)}
                  >
                    <div className="font-medium">{option.label}</div>
                    <div className="text-sm text-gray-500">{option.description}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Images Tab */}
        {activeTab === 'images' && (
          <div className="space-y-6">
            {/* Upload Section */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Add Images</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Button
                    onClick={() => fileInputRef.current?.click()}
                    disabled={isUploading}
                    className="w-full"
                  >
                    <Upload className="w-4 h-4 mr-2" />
                    {isUploading ? 'Uploading...' : 'Upload Images'}
                  </Button>
                  <input
                    ref={fileInputRef}
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={(e) => handleImageUpload(e.target.files)}
                    className="hidden"
                  />
                </div>

                <div className="relative">
                  <div className="absolute inset-0 flex items-center">
                    <div className="w-full border-t border-gray-300" />
                  </div>
                  <div className="relative flex justify-center text-sm">
                    <span className="bg-white px-2 text-gray-500">or</span>
                  </div>
                </div>

                <div className="space-y-2">
                  <Label>Generate with AI</Label>
                  <div className="flex space-x-2">
                    <Input
                      placeholder="Describe the image you want..."
                      onKeyPress={(e) => {
                        if (e.key === 'Enter') {
                          handleGenerateImage(e.currentTarget.value);
                          e.currentTarget.value = '';
                        }
                      }}
                    />
                    <Button
                      onClick={(e) => {
                        const input = e.currentTarget.previousElementSibling as HTMLInputElement;
                        handleGenerateImage(input.value);
                        input.value = '';
                      }}
                      disabled={isUploading}
                    >
                      <Wand2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Current Images */}
            {page.images && page.images.length > 0 && (
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">Page Images ({page.images.length})</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    {page.images.map((imageUrl: string, index: number) => (
                      <div key={index} className="flex items-center space-x-3 p-3 border rounded-lg">
                        <img
                          src={imageUrl}
                          alt={`Page image ${index + 1}`}
                          className="w-16 h-16 object-cover rounded"
                        />
                        <div className="flex-1">
                          <p className="text-sm font-medium">Image {index + 1}</p>
                          <p className="text-xs text-gray-500 truncate max-w-48">{imageUrl}</p>
                        </div>
                        <div className="flex items-center space-x-1">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => moveImage(index, 'up')}
                            disabled={index === 0}
                          >
                            ↑
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => moveImage(index, 'down')}
                            disabled={index === page.images.length - 1}
                          >
                            ↓
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => removeImage(index)}
                          >
                            <Trash2 className="w-3 h-3" />
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}
          </div>
        )}

        {/* Theme Tab */}
        {activeTab === 'theme' && (
          <div className="space-y-4">
            <div>
              <Label>Override Theme (Optional)</Label>
              <p className="text-sm text-gray-500 mb-3">
                Apply a different theme to this page only
              </p>

              <div className="grid grid-cols-1 gap-3">
                <div
                  className={`
                    p-3 border rounded-lg cursor-pointer transition-all
                    ${!page.theme_override ? 'border-blue-500 bg-blue-50' : 'border-gray-200 hover:border-gray-300'}
                  `}
                  onClick={() => handlePageUpdate('theme_override', null)}
                >
                  <div className="font-medium">Use Book Theme</div>
                  <div className="text-sm text-gray-500">Follow the main book theme</div>
                </div>

                {themes
                  .filter(theme => theme.category === 'page' || theme.category === 'complete')
                  .map((theme) => (
                    <div
                      key={theme.id}
                      className={`
                        p-3 border rounded-lg cursor-pointer transition-all
                        ${page.theme_override?.theme_id === theme.id
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-gray-300'
                        }
                      `}
                      onClick={() => handlePageUpdate('theme_override', { theme_id: theme.id })}
                    >
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="font-medium">{theme.display_name}</div>
                          <div className="text-sm text-gray-500">{theme.category}</div>
                        </div>
                        {theme.is_premium && (
                          <Badge variant="secondary">Premium</Badge>
                        )}
                      </div>
                    </div>
                  ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

app/api/v1/books/route.ts.content:
import { createRouteHandlerClient } from "@supabase/auth-helpers-nextjs";
import { cookies } from "next/headers";
import { NextResponse } from "next/server";

export async function GET(req: Request) {
  const supabase = createRouteHandlerClient({ cookies });

  try {
    // Check if user is authenticated
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser();

    if (userError || !user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    // Get URL parameters for pagination and filtering
    const { searchParams } = new URL(req.url);
    const page = parseInt(searchParams.get("page") || "1");
    const limit = Math.min(parseInt(searchParams.get("limit") || "10"), 50);
    const status = searchParams.get("status");

    let query = supabase
      .from("books")
      .select(`
        id,
        title,
        subtitle,
        author_name,
        isbn,
        dimensions,
        status,
        total_pages,
        created_at,
        updated_at,
        stories (
          id,
          story_title
        )
      `, { count: "exact" })
      .eq("user_id", user.id);

    // Apply filters
    if (status && status !== "all") {
      query = query.eq("status", status);
    }

    // Apply sorting and pagination
    query = query.order("updated_at", { ascending: false });
    const from = (page - 1) * limit;
    const to = from + limit - 1;
    query = query.range(from, to);

    const { data: books, error, count } = await query;

    if (error) {
      return NextResponse.json({ error: error.message }, { status: 400 });
    }

    // Calculate pagination metadata
    const totalPages = count ? Math.ceil(count / limit) : 1;
    const hasNextPage = page < totalPages;
    const hasPreviousPage = page > 1;

    return NextResponse.json({
      books: books || [],
      pagination: {
        currentPage: page,
        totalPages,
        totalCount: count || 0,
        hasNextPage,
        hasPreviousPage,
        limit,
      },
    });
  } catch (err: any) {
    return NextResponse.json(
      { error: err.message || "Internal Server Error" },
      { status: 500 }
    );
  }
}

export async function POST(req: Request) {
  const supabase = createRouteHandlerClient({ cookies });

  try {
    // Check if user is authenticated
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser();

    if (userError || !user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const {
      title,
      subtitle,
      author_name,
      isbn,
      dimensions,
      theme_id,
      cover_theme_id,
      story_id,
      metadata,
      pages
    } = await req.json();

    // Validate required fields
    if (!title || !author_name) {
      return NextResponse.json({
        error: "Title and author name are required"
      }, { status: 400 });
    }

    // Create book
    const { data: book, error: bookError } = await supabase
      .from("books")
      .insert({
        user_id: user.id,
        story_id: story_id || null,
        title,
        subtitle: subtitle || null,
        author_name,
        isbn: isbn || null,
        dimensions: dimensions || "A4",
        theme_id: theme_id || null,
        cover_theme_id: cover_theme_id || null,
        total_pages: pages ? pages.length : 0,
        status: "draft",
        metadata: metadata || {}
      })
      .select()
      .single();

    if (bookError) {
      return NextResponse.json({ error: bookError.message }, { status: 400 });
    }

    // Create pages if provided
    if (pages && Array.isArray(pages) && pages.length > 0) {
      const pagesData = pages.map((page, index) => ({
        book_id: book.id,
        page_number: page.page_number || index + 1,
        page_type: page.page_type || 'content',
        title: page.title || '',
        content: page.content || '',
        images: page.images || [],
        theme_override: page.theme_override || null,
        layout: page.layout || 'standard'
      }));

      const { error: pagesError } = await supabase
        .from("book_pages")
        .insert(pagesData);

      if (pagesError) {
        console.error("Failed to create pages:", pagesError);
        // Don't fail the book creation, just log the error
      }
    }

    return NextResponse.json({ book }, { status: 201 });
  } catch (err: any) {
    return NextResponse.json(
      { error: err.message || "Internal Server Error" },
      { status: 500 }
    );
  }
}

